["init"] = {
["custom"] = "local VERSION = 2\n\nlocal function Init()\n    if LiquidWeakAuras and (LiquidWeakAuras.VERSION or 0) >= VERSION then return end\n    \n    local aura_env = aura_env\n    \n    if not aura_env.saved then aura_env.saved = {} end\n    \n    _G[\"LiquidWeakAuras\"] = {}\n    \n    LiquidWeakAuras.VERSION = VERSION\n    \n    aura_env.initialized = true\n    aura_env.storedUnits = {}\n    \n    local ttsVoices = {\n        [1] = \"Davis\",\n        [2] = \"Maise\",\n        [3] = \"Sara\",\n        [4] = \"Steffan\"\n    }\n    \n    local inspect = LibStub:GetLibrary(\"LibGroupInSpecT-1.1\", true)\n    local openRaid = LibStub:GetLibrary(\"LibOpenRaid-1.0\", true)\n    local AceComm = LibStub(\"AceComm-3.0\")\n    \n    local numberToMark = {\n        \"star\", \"circle\", \"diamond\", \"triangle\", \"moon\", \"square\", \"cross\", \"skull\"\n    }\n    \n    local markToNumber = {\n        -- Raid Target Icon (ID)\n        [\"{rt1}\"] = 1,\n        [\"{rt2}\"] = 2,\n        [\"{rt3}\"] = 3,\n        [\"{rt4}\"] = 4,\n        [\"{rt5}\"] = 5,\n        [\"{rt6}\"] = 6,\n        [\"{rt7}\"] = 7,\n        [\"{rt8}\"] = 8,\n        \n        -- Raid Target Icon (ENG)\n        [\"{star}\"] = 1,\n        [\"{circle}\"] = 2,\n        [\"{diamond}\"] = 3,\n        [\"{triangle}\"] = 4,\n        [\"{moon}\"] = 5,\n        [\"{square}\"] = 6,\n        [\"{cross}\"] = 7,\n        [\"{skull}\"] = 8,\n        \n        -- Raid Target Icon (DE)\n        [\"{stern}\"] = 1,\n        [\"{kreis}\"] = 2,\n        [\"{diamant}\"] = 3,\n        [\"{dreieck}\"] = 4,\n        [\"{mond}\"] = 5,\n        [\"{quadrat}\"] = 6,\n        [\"{kreuz}\"] = 7,\n        [\"{totenschädel}\"] = 8,\n        \n        -- Raid Target Icon (FR)\n        [\"{étoile}\"] = 1,\n        [\"{cercle}\"] = 2,\n        [\"{losange}\"] = 3,\n        [\"{lune}\"] = 5,\n        [\"{carré}\"] = 6,\n        [\"{croix}\"] = 7,\n        [\"{crâne}\"] = 8,\n        \n        -- Raid Target Icon (IT)\n        [\"{stella}\"] = 1,\n        [\"{cerchio}\"] = 2,\n        [\"{rombo}\"] = 3,\n        [\"{triangolo}\"] = 4,\n        [\"{luna}\"] = 5,\n        [\"{quadrato}\"] = 6,\n        [\"{croce}\"] = 7,\n        [\"{teschio}\"] = 8,\n        \n        -- Raid Target Icon (ES)\n        [\"{estrella}\"] = 1,\n        [\"{círculo}\"] = 2,\n        [\"{diamante}\"] = 3,\n        [\"{triángulo}\"]= 4,\n        [\"{cuadrado}\"] = 6,\n        [\"{cruz}\"] = 7,\n        [\"{calavera}\"] = 8,\n        \n        -- Raid Target Icon (RU)\n        [\"{звезда}\"] = 1,\n        [\"{круг}\"] = 2,\n        [\"{ромб}\"] = 3,\n        [\"{треугольник}\"] = 4,\n        [\"{полумесяц}\"] = 5,\n        [\"{квадрат}\"] = 6,\n        [\"{крест}\"] = 7,\n        [\"{череп}\"] = 8,\n        \n        -- Raid Target Icon (CN)\n        [\"{星形}\"] = 1,\n        [\"{圆形}\"] = 2,\n        [\"{菱形}\"] = 3,\n        [\"{三角}\"] = 4,\n        [\"{月亮}\"] = 5,\n        [\"{方块}\"] = 6,\n        [\"{十字}\"] = 7,\n        [\"{骷髅}\"] = 8,\n        \n        -- Raid Target Icon (KR)\n        [\"{별}\"] = 1,\n        [\"{동그라미}\"] = 2,\n        [\"{다이아몬드}\"] = 3,\n        [\"{세모}\"] = 4,\n        [\"{달}\"] = 5,\n        [\"{네모}\"] = 6,\n        [\"{가위표}\"] = 7,\n        [\"{해골}\"] = 8,\n    }\n    \n    local specToType = {\n        -- Death Knight\n        [250] = \"m\", -- Blood\n        [251] = \"m\", -- Frost\n        [252] = \"m\", -- Unholy\n        -- Demon Hunter\n        [577] = \"m\", -- Havoc\n        [581] = \"m\", -- Vengeance\n        -- Druid\n        [102] = \"r\", -- Balance \n        [103] = \"m\", -- Feral \n        [104] = \"m\", -- Guardian \n        [105] = \"r\", -- Restoration\n        -- Hunter\n        [253] = \"r\", -- Beast Mastery\n        [254] = \"r\", -- Marksmanship\n        [255] = \"m\", -- Survival\n        -- Mage\n        [62] = \"r\", -- Arcane\n        [63] = \"r\", -- Fire\n        [64] = \"r\", -- Frost\n        -- Monk\n        [268] = \"m\", -- Brewmaster\n        [270] = \"m\", -- Mistweaver\n        [269] = \"m\", -- Windwalker\n        -- Paladin\n        [65] = \"m\", -- Holy  \n        [66] = \"m\", -- Protection  \n        [70] = \"m\", -- Retribution\n        -- Priest\n        [256] = \"r\", -- Discipline \n        [257] = \"r\", -- Holy   \n        [258] = \"r\", -- Shadow\n        -- Rogue\n        [259] = \"m\", -- Assassination  \n        [260] = \"m\", -- Outlaw  \n        [261] = \"m\", -- Subtlety\n        -- Shaman\n        [262] = \"r\", -- Elemental  \n        [263] = \"m\", -- Enhancement\n        [264] = \"r\", -- Restoration\n        -- Warlock\n        [265] = \"r\", -- Affliction \n        [266] = \"r\", -- Demonology  \n        [267] = \"r\", -- Destruction\n        -- Warrior\n        [71] = \"m\", -- Arms  \n        [72] = \"m\", -- Fury  \n        [73] = \"m\", -- Protection\n        -- Evoker\n        [1467] = \"r\", -- Devastation\n        [1468] = \"r\", -- Preservation\n        [1473] = \"r\", -- Augmentation\n    }\n    \n    local classToType = {\n        WARRIOR = \"m\", -- Warrior\n        PALADIN = \"m\", -- Paladin\n        ROGUE = \"m\", -- Rogue\n        PRIEST = \"r\", -- Priest\n        DEATHKNIGHT = \"m\", -- Death Knight\n        MAGE = \"r\", -- Mage\n        WARLOCK = \"r\", -- Warlock\n        MONK = \"m\", -- Monk\n        DEMONHUNTER = \"m\", -- Demon Hunter\n        EVOKER = \"r\", -- Evoker\n    }\n    \n    local roleClassToSpec = {\n        DAMAGER = {\n            PALADIN = 70, -- Retribution\n            PRIEST = 258, -- Shadow\n            MONK = 269, -- Windwalker\n            DEMONHUNTER = 577, -- Havoc\n        },\n        HEALER = {\n            PALADIN = 65, -- Holy\n            SHAMAN = 264, -- Restoration\n            MONK = 270, -- Mistweaver\n            DRUID = 105, -- Restoration\n            EVOKER = 1468, -- Preservation\n        },\n        TANK = {\n            WARRIOR = 73, -- Protection\n            PALADIN = 66, -- Protection\n            DEATHKNIGHT = 250, -- Blood\n            MONK = 268, -- Brewmaster\n            DRUID = 104, -- Guardian\n            DEMONHUNTER = 581, -- Vengeance\n        },\n        NONE = {}\n    }\n    \n    aura_env.meleeSpells = {\n        [186270] = 255, -- Raptor Strike (Survival)\n        [17364] = 263, -- Stormstrike (Enhancement)\n        [5217] = 103, -- Tiger's Fury (Feral)\n    }\n    \n    function LiquidWeakAuras:MarkToNumber(mark)\n        return mark and markToNumber[mark]\n    end\n    \n    -- Changes {star}, {stern} etc. into {rt1} so WeakAuras text displays it correctly\n    local gsubMarks = {}\n    \n    for mark, number in pairs(markToNumber) do\n        gsubMarks[mark] = string.format(\"{rt%d}\", number)\n    end\n    \n    function LiquidWeakAuras:gsubMarks(text)\n        return text:gsub(\"{.-}\", gsubMarks)\n    end\n    \n    -- Returns \"m\" or \"r\"\n    -- Second return value is the unit's spec ID, but is not guaranteed\n    function LiquidWeakAuras:Spec(unit)\n        if unit and UnitExists(unit) then\n            local GUID = UnitGUID(unit)\n            \n            -- If we already requested (or received) information from this unit before, use it\n            local storedSpec = aura_env.storedUnits[GUID]\n            \n            if storedSpec then\n                return specToType[storedSpec], storedSpec\n            end\n            \n            -- Use a combination of the unit's role and class to determine spec\n            local role = UnitGroupRolesAssigned(unit)\n            local class = UnitClassBase(unit)\n            \n            if class then\n                local spec = roleClassToSpec[role][class]\n                \n                if spec then\n                    aura_env.storedUnits[GUID] = spec\n                    return specToType[spec], spec\n                end\n            end\n            \n            -- WeakAuras built-in spec detection\n            local waSpec = WeakAuras.SpecForUnit(unit)\n            \n            if waSpec then\n                aura_env.storedUnits[GUID] = waSpec\n                return specToType[waSpec], waSpec\n            end\n            \n            -- Open Raid Library (Details!)\n            local name, realm = UnitName(unit)\n            local myRealm = GetNormalizedRealmName()\n            local fullName = format(\"%s-%s\", name, realm or myRealm)\n            \n            if openRaid then\n                if openRaid.GetUnitInfo then\n                    local info = openRaid.GetUnitInfo(unit)\n                    \n                    if info and info.specId and specToType[info.specId] then\n                        aura_env.storedUnits[GUID] = info.specId\n                        return specToType[info.specId], info.specId\n                    end\n                elseif openRaid.playerInfoManager then\n                    local info = openRaid.playerInfoManager.GetPlayerInfo(name) or openRaid.playerInfoManager.GetPlayerInfo(fullName)\n                    \n                    if info and info.specId and specToType[info.specId] then\n                        aura_env.storedUnits[GUID] = info.specId\n                        return specToType[info.specId], info.specId\n                    end\n                end\n            end\n            \n            -- LibGroupInSpecT\n            if inspect and GUID then\n                local info = inspect:GetCachedInfo(GUID)\n                \n                if info and info.global_spec_id and specToType[info.global_spec_id] then\n                    aura_env.storedUnits[GUID] = info.global_spec_id\n                    return specToType[info.global_spec_id], info.global_spec_id\n                end\n            end\n            \n            -- MRT\n            if VExRT and VExRT.ExCD2 and VExRT.ExCD2.gnGUIDs then\n                local spec = VExRT.ExCD2.gnGUIDs[name] or VExRT.ExCD2.gnGUIDs[fullName]\n                \n                if spec and specToType[spec] then\n                    aura_env.storedUnits[GUID] = spec\n                    return specToType[spec], spec\n                end\n            end\n            \n            -- Class check (no spec information)\n            if class and classToType[class] then \n                return classToType[class]\n            end\n            \n            -- Default to ranged (no spec information)\n            return \"r\"\n        end\n    end\n    \n    -- Sort an array of player GUIDs by spec type\n    -- By default sorts melee specs to the front\n    function LiquidWeakAuras:SortTable(t, rangedFirst, sortRoles)\n        table.sort(\n            t,\n            function (GUID1, GUID2)\n                if not GUID1 then return false end\n                if not GUID2 then return true end\n                \n                local unit1 = aura_env.GUIDToUnit[GUID1] or UnitTokenFromGUID(GUID1)\n                local unit2 = aura_env.GUIDToUnit[GUID2] or UnitTokenFromGUID(GUID2)\n                \n                local type1, spec1 = LiquidWeakAuras:Spec(unit1)\n                local type2, spec2 = LiquidWeakAuras:Spec(unit2)\n                \n                -- Sort dps < healers < tanks if sortRoles is enabled\n                if sortRoles then\n                    local role1 = UnitGroupRolesAssigned(unit1)\n                    local role2 = UnitGroupRolesAssigned(unit2)\n                    \n                    if role1 ~= role2 then\n                        return role1 < role2\n                    end\n                end\n                \n                if type1 and type2 and type1 ~= type2 then\n                    return type1 == (rangedFirst and \"r\" or \"m\")\n                elseif spec1 and spec2 and spec1 ~= spec2 then\n                    return spec1 < spec2 -- For consistency's sake\n                else\n                    return GUID1 < GUID2\n                end\n            end\n        )\n    end\n    \n    -- Sort an array of player GUIDs by class mobility (slow -> fast)\n    local classMobility = {\n        PRIEST = 1,\n        DEATHKNIGHT = 2,\n        WARLOCK = 3,\n        PALADIN = 4,\n        DRUID = 5,\n        HUNTER = 6,\n        SHAMAN = 7,\n        ROGUE = 8,\n        EVOKER = 9,\n        WARRIOR = 10,\n        DEMONHUNTER = 11,\n        MONK = 12,\n        MAGE = 13\n    }\n    \n    function LiquidWeakAuras:SortTableMobility(t, reverse)\n        table.sort(\n            t,\n            function (GUID1, GUID2)\n                if not GUID1 then return false end\n                if not GUID2 then return true end\n                \n                local unit1 = aura_env.GUIDToUnit[GUID1] or UnitTokenFromGUID(GUID1)\n                local unit2 = aura_env.GUIDToUnit[GUID2] or UnitTokenFromGUID(GUID2)\n                \n                local class1 = UnitClassBase(unit1)\n                local class2 = UnitClassBase(unit2)\n                \n                local mobility1 = classMobility[class1]\n                local mobility2 = classMobility[class2]\n                \n                if reverse then\n                    if mobility1 ~= mobility2 then\n                        return mobility1 > mobility2\n                    else\n                        return GUID1 > GUID2\n                    end\n                else\n                    if mobility1 ~= mobility2 then\n                        return mobility1 < mobility2\n                    else\n                        return GUID1 < GUID2\n                    end\n                end\n            end\n        )\n    end\n    \n    -- Broadcast using AceComm while in a group\n    -- The specified event will be called using WeakAuras.ScanEvents with any number of args provided (of type string or number)\n    function LiquidWeakAuras:Broadcast(event, ...)\n        if GetNumGroupMembers() > 0 then\n            local chatType = IsInGroup(LE_PARTY_CATEGORY_INSTANCE) and \"INSTANCE_CHAT\" or IsInRaid() and \"RAID\" or \"PARTY\"\n            local message = event\n            local argTable = {...}\n            local argCount = select(\"#\", ...)\n            \n            for i = 1, argCount do\n                local functionArg = argTable[i]\n                local argType = type(functionArg)\n                \n                -- If the arg is of a type that we cannot handle, send an empty string instead\n                -- These get converted to nil in the receive function\n                if argType ~= \"string\" and argType ~= \"number\" and argType ~= \"boolean\" then\n                    functionArg = \"\"\n                    argType = \"string\"\n                end\n                \n                message = string.format(\"%s:%s(%s)\", message, tostring(functionArg), argType)\n            end\n            \n            AceComm:SendCommMessage(\"LiquidWA_MSG\", message, chatType)\n        else -- If we're not in a group, just send the event directly\n            WeakAuras.ScanEvents(event, ...)\n        end\n    end\n    \n    -- Handle LiquidWA_MSG addon messages\n    local function ReceiveComm(text)\n        local argTable = {strsplit(\":\", text)}\n        local formattedArgTable = {}\n        local event = argTable[1]\n        \n        table.remove(argTable, 1)\n        \n        for _, functionArg in ipairs(argTable) do\n            local argValue, argType = functionArg:match(\"(.*)%((%a+)%)\")\n            \n            if argType == \"number\" then\n                argValue = tonumber(argValue)\n            elseif argType == \"boolean\" then\n                argValue = argValue == \"true\"\n            end\n            \n            if argValue == \"\" then\n                table.insert(formattedArgTable, false)\n            else\n                table.insert(formattedArgTable, argValue)\n            end\n        end\n        \n        WeakAuras.ScanEvents(event, unpack(formattedArgTable))\n    end\n    \n    AceComm:RegisterComm(\n        \"LiquidWA_MSG\",\n        function(_, text, channel)\n            if channel == \"WHISPER\" then return end\n            if channel == \"GUILD\" then return end\n            if channel == \"CHANNEL\" then return end\n            \n            ReceiveComm(text)\n        end\n    )\n    \n    -- Plays a sound from LiquidMedia\n    -- If the sound (or LiquidMedia itself) is not available, play the sound through TTS\n    -- If a voice is not specified, use the default voice that is configured in custom options\n    function LiquidWeakAuras:PlaySound(sound, voice)\n        if not sound then return end\n        \n        -- Convert {rt1} etc. to word\n        local markNumber = markToNumber[sound]\n        \n        if markNumber then\n            sound = numberToMark[markNumber]\n        end\n        \n        sound = tostring(sound)\n        \n        local soundFile = string.format(\"%s\\\\%s.mp3\", voice or aura_env.ttsVoice, sound)\n        local soundPlayed = false\n        \n        if aura_env.config.preferAuraUpdaterTTS then\n            if C_AddOns.IsAddOnLoaded(\"LiquidMedia\") then -- Try to play a sound file from LiquidMedia\n                soundPlayed = PlaySoundFile(\"Interface\\\\AddOns\\\\LiquidMedia\\\\TTS\\\\\" .. soundFile, \"Master\")\n            elseif C_AddOns.IsAddOnLoaded(\"AuraUpdater\") then -- Try to play a sound from AuraUpdater\n                soundPlayed = PlaySoundFile(\"Interface\\\\AddOns\\\\AuraUpdater\\\\Media\\\\TTS\\\\\" .. soundFile, \"Master\")\n            end\n        end\n        \n        if soundPlayed then return end\n        \n        C_VoiceChat.SpeakText(\n            1,\n            sound,\n            Enum.VoiceTtsDestination.LocalPlayback,\n            C_TTSSettings and C_TTSSettings.GetSpeechRate() or 0,\n            C_TTSSettings and C_TTSSettings.GetSpeechVolume() or 100\n        )\n    end\n    \n    -- Returns the npc ID, spawn time, and spawn index for a creature\n    -- Takes either a unit or a GUID as input\n    function LiquidWeakAuras:CreatureInfo(GUID)\n        if UnitExists(GUID) then -- If a unit ID was passed instead of GUID, convert it\n            GUID = UnitGUID(GUID)\n        end\n        \n        local unitType, _, _, _, _, npcID, spawnUID = strsplit(\"-\", GUID)\n        \n        if unitType == \"Creature\" or unitType == \"Vehicle\" then\n            local spawnEpoch = GetServerTime() - (GetServerTime() % 2^23)\n            local spawnEpochOffset = bit.band(tonumber(string.sub(spawnUID, 5), 16), 0x7fffff)\n            local spawnIndex = bit.rshift(bit.band(tonumber(string.sub(spawnUID, 1, 5), 16), 0xffff8), 3)\n            local spawnTime = spawnEpoch + spawnEpochOffset\n            \n            if spawnTime > GetServerTime() then\n                -- This only occurs if the epoch has rolled over since a unit has spawned.\n                spawnTime = spawnTime - ((2^23) - 1)\n            end\n            \n            return tonumber(npcID), spawnTime, spawnIndex\n        end\n    end\n    \n    -- Utility function for iterating MRT note assignments\n    -- Usage: <for lineCount, line in LiquidWeakAuras:IterateNoteAssignment([number]) do>\n    function LiquidWeakAuras:IterateNoteAssignment(identifier, noteOverride)\n        local startLine, endLine\n        \n        local note = noteOverride or (VMRT and VMRT.Note and VMRT.Note.Text1)\n        local lineCount = 1\n        local lines = {}\n        \n        if not identifier then\n            startLine = \"liquidstart\"\n            endLine = \"liquidend\"\n        elseif type(identifier) == \"number\" then\n            startLine = string.format(\"liquidstart%d\", identifier)\n            endLine = string.format(\"liquidend%d\", identifier)\n        elseif type(identifier) == \"string\" then\n            startLine = string.format(\"liquid%sstart\", identifier:lower())\n            endLine = string.format(\"liquid%send\", identifier:lower())\n        end\n        \n        if note then\n            local betweenLine = false\n            \n            for line in note:gmatch(\"[^\\r\\n]+\") do\n                if strtrim(line):lower() == endLine then\n                    betweenLine = false\n                end\n                \n                if betweenLine then\n                    table.insert(lines, line)\n                end\n                \n                if strtrim(line):lower() == startLine then\n                    betweenLine = true\n                end\n            end\n        end\n        \n        return function()\n            local line = lines[lineCount]\n            \n            if line then\n                lineCount = lineCount + 1\n                \n                return lineCount - 1, line\n            end\n        end\n    end\n    \n    -- Returns whether MRT contains the keyword to ignore assignments\n    function LiquidWeakAuras:IsAssignmentsDisabled()\n        local note = VMRT and VMRT.Note and VMRT.Note.Text1\n        \n        if note then\n            for line in note:gmatch(\"[^\\r\\n]+\") do\n                if strtrim(line):lower() == \"liquiddisable\" then\n                    return true\n                end\n            end\n        end\n    end\n    \n    -- Gets a unit's nickname from either LiquidAPI or AuraUpdater (in that order)\n    -- If no nickname exists in either, return the unit's regular name\n    function LiquidWeakAuras:GetNickname(unit)\n        local name = UnitNameUnmodified(unit)\n        \n        -- Liquid\n        local liquidNickname = LiquidAPI and LiquidAPI:GetName(unit)\n        \n        if liquidNickname and liquidNickname ~= name then\n            return liquidNickname\n        end\n        \n        -- AuraUpdater\n        local auraUpdaterNickname = AuraUpdater and AuraUpdater.GetNickname and AuraUpdater:GetNickname(unit)\n        \n        if auraUpdaterNickname and auraUpdaterNickname ~= name then\n            return auraUpdaterNickname\n        end\n        \n        return name\n    end\n    \n    -- Returns an array of GUIDs (sorted) of all units in a certain raid subgroup\n    local function groupToGUIDArray(groupNumber)\n        local GUIDs = {}\n        \n        for raidIndex = 1, 40 do\n            local _, _, subgroup = GetRaidRosterInfo(raidIndex)\n            \n            if subgroup == groupNumber then\n                local unit = \"raid\" .. raidIndex\n                local GUID = UnitGUID(unit)\n                \n                if GUID then\n                    table.insert(GUIDs, GUID)\n                end\n            end\n        end\n        \n        table.sort(GUIDs)\n        \n        return GUIDs\n    end\n    \n    -- Transforms a string of (nick)names to a sorted array of GUIDs\n    -- Optionally allow for players to be present in the array multiple times\n    -- Returns whether the player's GUID is included\n    -- If the first word of the line is a marker, returns the marker number\n    function LiquidWeakAuras:LineToGUIDArray(line, allowDuplicates)\n        local guidArray = {}\n        local nameToGUID = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            local name = UnitName(unit)\n            local nickname = LiquidWeakAuras:GetNickname(unit)\n            \n            if name then\n                nameToGUID[name:lower()] = GUID\n            end\n            \n            if nickname then\n                nameToGUID[nickname:lower()] = GUID\n            end\n        end\n        \n        line = line:gsub(\"||c%x%x%x%x%x%x%x%x\", \"\")\n        line = line:gsub(\"||r\", \"\")\n        line = line:lower()\n        line = LiquidWeakAuras:gsubMarks(line) -- Format all marks as {rt1} instead of {star}/{stern}/etc.\n        \n        local mark = line:match(\"^{rt(%d)}\")\n        \n        -- Remove the mark from the line, in case the user forgot to add a space between them\n        if mark then\n            line = line:match(\"{rt%d}%s-(.+)\")\n        end\n        \n        for word in line:gmatch(\"%S+\") do\n            local subgroup = word:match(\"^group(%d)$\")\n            \n            -- If the word is group<number>, add all GUIDs in that subgroup\n            -- If not, treat it as a player name\n            if subgroup then\n                subgroup = tonumber(subgroup)\n                \n                local GUIDs = groupToGUIDArray(subgroup)\n                \n                for _, GUID in ipairs(GUIDs) do\n                    if allowDuplicates then\n                        table.insert(guidArray, GUID)\n                    else\n                        tInsertUnique(guidArray, GUID)\n                    end\n                end\n            else\n                local GUID = nameToGUID[word]\n                \n                if GUID then\n                    if allowDuplicates then\n                        table.insert(guidArray, GUID)\n                    else\n                        tInsertUnique(guidArray, GUID)\n                    end\n                end\n            end\n        end\n        \n        return guidArray, tContains(guidArray, WeakAuras.myGUID), tonumber(mark)\n    end\n    \n    -- If AuraUpdater is not installed, supply the custom grow functions here\n    -- Only grow direction and spacing are supported without aura updater\n    if not AuraUpdater then\n        _G[\"AuraUpdater\"] = {}\n        \n        local AuraUpdater = AuraUpdater\n        local anchorSettings = {}\n        local updateAnchorFunctions = {}\n        local applyAnchorFunctions = {}\n        \n        -- Number of clones that show in preview mode (with options open)\n        -- Used for correcting aura positions during preview\n        local previewCounts = {\n            bars = 3,\n            specialBars = 1,\n            lists = 10,\n            raidLeaderLists = 4,\n            bigIcons = 1,\n            icons = 3,\n            assignments = 1,\n            texts = 1,\n            tankWarningsBars = 2, -- It's actually 1, but needs to be 2 here to account for the group having both texts and bars\n            tankWarningsTexts = 1,\n            coTankWarnings = 2,\n            tankIcons = 2,\n            coTankIcons = 2\n        }\n        \n        local function PositionAuras(settings, newPositions, activeRegions)\n            if not settings then\n                for regionCount in ipairs(activeRegions) do\n                    newPositions[regionCount] = {0, 0}\n                end\n                \n                return\n            end\n            \n            local nextPosition = WeakAuras.IsOptionsOpen() and settings.optionsOffsets and CopyTable(settings.optionsOffsets) or {0, 0}\n            local limit = settings.limit or #activeRegions\n            local directionX = settings.grow == \"RIGHT\" and 1 or settings.grow == \"LEFT\" and -1 or 0\n            local directionY = settings.grow == \"UP\" and 1 or settings.grow == \"DOWN\" and -1 or 0\n            \n            for regionCount, regionData in ipairs(activeRegions) do\n                if regionCount > limit then\n                    newPositions[regionCount] = {0, 0, false}\n                else\n                    local skipPosition = regionData.region.state.skipPosition\n                    \n                    newPositions[regionCount] = nextPosition\n                    \n                    if not skipPosition then\n                        nextPosition = {\n                            nextPosition[1] + directionX * (regionData.region.width + settings.space),\n                            nextPosition[2] + directionY * (regionData.region.height + settings.space)\n                        }\n                    end\n                end\n            end\n        end\n        \n        local function GetAuraSettings(groupName, auraName, previewCount)\n            local groupData = WeakAuras.GetData(groupName)\n            local auraData = WeakAuras.GetData(auraName)\n            \n            if not (groupData and auraData) then return end\n            \n            local settings = {}\n            \n            settings.grow = groupData.grow\n            settings.limit = groupData.useLimit and groupData.limit or 100\n            settings.space = groupData.space\n            \n            settings.width = auraData.width\n            settings.height = auraData.height\n            \n            -- Calculate options offsets\n            settings.optionsOffsets = {0, 0}\n            \n            if auraData.regionType == \"icon\" then\n                if settings.grow == \"UP\" then\n                    settings.optionsOffsets[2] = -settings.height - settings.space\n                elseif settings.grow == \"RIGHT\" then\n                    settings.optionsOffsets[1] = -(previewCount - 1) * (settings.width + settings.space)\n                end\n            else -- Bar or Text\n                if settings.grow == \"UP\" then\n                    settings.optionsOffsets[2] = -(previewCount - 1) * (settings.height + settings.space)\n                end\n            end\n            \n            return settings\n        end\n        \n        updateAnchorFunctions.Bars = function() anchorSettings.Bars = GetAuraSettings(\"Liquid Anchor - Bars\", \"Liquid Anchor - Bar\", previewCounts.bars) end\n        updateAnchorFunctions.SpecialBars = function() anchorSettings.SpecialBars = GetAuraSettings(\"Liquid Anchor - Special Bars\", \"Liquid Anchor - Special Bar\", previewCounts.specialBars) end\n        updateAnchorFunctions.Lists = function() anchorSettings.Lists = GetAuraSettings(\"Liquid Anchor - Lists\", \"Liquid Anchor - List\", previewCounts.lists) end\n        updateAnchorFunctions.RaidLeaderLists = function() anchorSettings.RaidLeaderLists = GetAuraSettings(\"Liquid Anchor - Raid Leader Lists\", \"Liquid Anchor - Raid Leader List\", previewCounts.raidLeaderLists) end\n        updateAnchorFunctions.BigIcons = function() anchorSettings.BigIcons = GetAuraSettings(\"Liquid Anchor - Big Icons\", \"Liquid Anchor - Big Icon\", previewCounts.bigIcons) end\n        updateAnchorFunctions.Icons = function() anchorSettings.Icons = GetAuraSettings(\"Liquid Anchor - Icons\", \"Liquid Anchor - Icon\", previewCounts.icons) end\n        updateAnchorFunctions.Texts = function() anchorSettings.Texts = GetAuraSettings(\"Liquid Anchor - Texts\", \"Liquid Anchor - Text\", previewCounts.texts) end\n        updateAnchorFunctions.Assignments = function() anchorSettings.Assignments = GetAuraSettings(\"Liquid Anchor - Assignments\", \"Liquid Anchor - Assignment\", previewCounts.assignments) end\n        updateAnchorFunctions.TankWarningsBars = function() anchorSettings.TankWarningsBars = GetAuraSettings(\"Liquid Anchor - Tank Warnings\", \"Liquid Anchor - Tank Warning Bar\", previewCounts.tankWarningsBars) end\n        updateAnchorFunctions.TankWarningsTexts = function() anchorSettings.TankWarningsTexts = GetAuraSettings(\"Liquid Anchor - Tank Warnings\", \"Liquid Anchor - Tank Warning Text\", previewCounts.tankWarningsTexts) end\n        updateAnchorFunctions.TankIcons = function() anchorSettings.TankIcons = GetAuraSettings(\"Liquid Anchor - Tank Icons\", \"Liquid Anchor - Tank Icon\", previewCounts.tankIcons) end\n        updateAnchorFunctions.CoTankIcons = function() anchorSettings.CoTankIcons = GetAuraSettings(\"Liquid Anchor - Co-Tank Icons\", \"Liquid Anchor - Co-Tank Icon\", previewCounts.coTankIcons) end\n        \n        -- Tank warnings don't use the generic implementation since the group has two types of auras (bars and texts)\n        applyAnchorFunctions.TankWarnings = function(newPositions, activeRegions)\n            local barSettings = anchorSettings.TankWarningsBars\n            local textSettings = anchorSettings.TankWarningsTexts\n            \n            if not (barSettings and textSettings) then return end\n            \n            local optionsOffsets = {\n                barSettings.optionsOffsets[1] + textSettings.optionsOffsets[1],\n                barSettings.optionsOffsets[2] + textSettings.optionsOffsets[2],\n            }\n            \n            -- Position is not done using PositionAuras(), since tank warnings are unique in that they have both bars and texts\n            -- Both barSettings and textSettings contains identical group settings data. We just use barSettings to fetch things like limit, spacing, etc.\n            local nextPosition = WeakAuras.IsOptionsOpen() and optionsOffsets and CopyTable(optionsOffsets) or {0, 0}\n            local limit = barSettings.limit or #activeRegions\n            local directionX = barSettings.grow == \"RIGHT\" and 1 or barSettings.grow == \"LEFT\" and -1 or 0\n            local directionY = barSettings.grow == \"UP\" and 1 or barSettings.grow == \"DOWN\" and -1 or 0\n            \n            for regionCount, regionData in ipairs(activeRegions) do\n                local region = regionData.region\n                \n                if regionCount > limit then\n                    newPositions[regionCount] = {0, 0, false}\n                else\n                    local skipPosition = region.state.skipPosition\n                    \n                    newPositions[regionCount] = nextPosition\n                    \n                    if not skipPosition then\n                        nextPosition = {\n                            nextPosition[1] + directionX * (regionData.region.width + barSettings.space),\n                            nextPosition[2] + directionY * (regionData.region.height + barSettings.space)\n                        }\n                    end\n                end\n            end\n        end\n        \n        function AuraUpdater:UpdateAnchorSettings(anchorType)\n            if not updateAnchorFunctions[anchorType] then return end\n            \n            updateAnchorFunctions[anchorType]()\n        end\n        \n        function AuraUpdater:ApplyAnchorSettings(anchorType, newPositions, activeRegions)\n            if applyAnchorFunctions[anchorType] then\n                applyAnchorFunctions[anchorType](newPositions, activeRegions)\n            else\n                PositionAuras(anchorSettings[anchorType], newPositions, activeRegions)\n            end\n        end\n        \n        -- Make sure all the settings are updated\n        for _, func in pairs(updateAnchorFunctions) do\n            func()\n        end\n    end\n    \n    -- Play a preview sound when the default text-to-speech voice is changed in custom options\n    aura_env.ttsVoice = ttsVoices[aura_env.config.ttsVoice]\n    \n    if aura_env.saved.ttsVoice and aura_env.config.ttsVoice ~= aura_env.saved.ttsVoice then\n        local sounds = {\"front\", \"back\", \"left\", \"right\", \"middle\"}\n        local randomSound = sounds[math.random(#sounds)]\n        \n        LiquidWeakAuras:PlaySound(randomSound)\n    end\n    \n    aura_env.saved.ttsVoice = aura_env.config.ttsVoice\nend\n\nInit()\n\n-- Incase the user has another copy of LiquidWeakAuras that doesn't check versions\n-- That older copy might override this on and cause issues\nC_Timer.After(1, Init)",
