["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 7,\n            expirationTime = GetTime() + 7,\n            first = math.random(2) == 1,\n            additionalProgress = {\n                {\n                    max = 7,\n                    min = 1.8\n                },\n                {\n                    max = 1.8,\n                    min = 0\n                }\n            }\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        if aura_env.disabled then return end\n        \n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1219607 then -- Eradicating Salvo\n            if not aura_env.assigned then return end\n            if destGUID == WeakAuras.myGUID then return end\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 7,\n                expirationTime = GetTime() + 7,\n                first = aura_env.first,\n                additionalProgress = {\n                    {\n                        max = 7,\n                        min = 1.8\n                    },\n                    {\n                        max = 1.8,\n                        min = 0\n                    }\n                },\n                autoHide = true\n            }\n            \n            return true\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        aura_env.assigned = false\n        \n        for lineNumber, line in LiquidWeakAuras:IterateNoteAssignment(\"Salvo\") do\n            local _, containsPlayerGUID = LiquidWeakAuras:LineToGUIDArray(line)\n            \n     