["custom"] = "aura_env.PYLON_DURATION = 23 -- Time between the Pylons becoming visible (not active) and disappearing\n\nfunction aura_env:ReadNoteAssignments()\n    -- [1] = Players soaking the first set of pylons each phase\n    -- [2] = Players soaking the second set of pylons each phase\n    aura_env.assignments = {{}, {}}\n    aura_env.backups = {}\n    \n    local lineCount = 0\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"Pylon\") do\n        local GUIDs, _, mark = LiquidWeakAuras:LineToGUIDArray(line)\n        \n        if next(GUIDs) then\n            lineCount = lineCount + 1\n            \n            if lineCount <= 8 and mark then\n                local setNumber = lineCount <= 4 and 1 or 2\n                \n                aura_env.assignments[setNumber][mark] = GUIDs\n            elseif lineCount == 9 then\n                aura_env.backups = GUIDs\n            end\n        end\n    end\nend",
["do_custom"] = true,
