},
["events"] = "CHAT_MSG_RAID_BOSS_WHISPER, ENCOUNTER_START",
["custom"] = "function(event, ...)\n    if event == \"CHAT_MSG_RAID_BOSS_WHISPER\" then\n        if aura_env.disabled then return end\n        \n        local text = ...\n        \n        if not text:match(\"spell:1246921\") then return end -- \"Infusion Pylons begin to turn on!\"\n        \n        aura_env.set = aura_env.set + 1\n        \n        if aura_env.set == 3 then\n            aura_env.set = 1\n        end\n        \n        local groups = CopyTable(aura_env.assignments[aura_env.set])\n        local backups = CopyTable(aura_env.backups)\n        \n        for mark = 1, 8 do\n            local GUIDs = groups[mark]\n            \n            if GUIDs then\n                -- Count number of dead players in the group\n                local deadCount = 0\n                \n                for _, GUID in ipairs(GUIDs) do\n                    local unit = aura_env.GUIDToUnit[GUID]\n                    local isDead = UnitIsDeadOrGhost(unit)\n                    \n                    if isDead then\n                        deadCount = deadCount + 1\n                    end\n                end\n                \n                -- Replace dead people with backups\n                for _ = 1, deadCount do\n                    if next(backups) then\n                        local backupGUID = table.remove(backups, 1)\n                        \n                        table.insert(GUIDs, backupGUID)\n                    end\n                end\n                \n                WeakAuras.ScanEvents(\"LIQUID_PYLON_SOAK_START\", GUIDs, mark)\n            end\n        end\n        \n        C_Timer.After(\n            aura_env.PYLON_DURATION,\n            function()\n                WeakAuras.ScanEvents(\"LIQUID_PYLON_SOAK_END\")\n            end\n        )\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        \n        aura_env.set = 0\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env:ReadNoteAssignments()\n    end\nend",
