["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 2 do\n            local affected = math.random(2) == 1\n            local dead = math.random(4) == 1\n            local tether = math.random(4) == 1\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 10,\n                expirationTime = GetTime() + (affected and math.random(10) or 0.01),\n                stacks = affected and math.random(10) or 0,\n                index = i,\n                unit = \"player\",\n                dead = dead,\n                tether = tether,\n                autoHide = false\n            }\n        end\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local GUIDs = aura_env.GUIDs -- GUIDs (in order) of the player's soak group\n        \n        -- If GUIDs is nil, that means the Pylons have disappeared\n        -- Hide all states\n        if not GUIDs then\n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n        \n        -- Create/update states for players in my group\n        for index, GUID in ipairs(GUIDs) do\n            local unit = aura_env.GUIDToUnit[GUID]\n            local isDead = UnitIsDeadOrGhost(unit)\n            local isTethered = WA_GetUnitDebuff(unit, 1226311) -- Infusion Tether\n            local _, _, stacks, _, duration, expirationTime = WA_GetUnitDebuff(unit, 1247045) -- Hyper Infusion\n            \n            allstates[GUID] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = duration or 45,\n                expirationTime = expirationTime or 0,\n                stacks = stacks or 0,\n                dead = isDead,\n                index = index,\n                tether = isTethered,\n                unit = unit,\n                autoHide = false\n            }\n        end\n        \n        return true\n    elseif event == \"LIQUID_PYLON_SOAK_START\" then\n        local GUIDs, mark = ...\n        \n        if tContains(GUIDs, WeakAuras.myGUID) then\n            aura_env.GUIDs = GUIDs\n            aura_env.mark = mark\n            \n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        end\n    elseif event == \"LIQUID_PYLON_SOAK_END\" then\n        aura_env.GUIDs = nil\n        aura_env.mark = nil\n        \n        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n    elseif event == \"LIQUID_UNIT_ALIVE\" then\n        local GUID = ...\n\n        if not aura_env.GUIDs then return end\n        if not tContains(aura_env.GUIDs, GUID) then return end\n\n        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n\n        if not aura_env.GUIDs then return end\n        if not tContains(aura_env.GUIDs, destGUID) then return end\n\n        if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" or subEvent == \"SPELL_AURA_REMOVED\" then\n            if spellID == 1226311 or spellID == 1247045 then -- Infusion Tether/Hyper Infusion\n                WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n            end\n        elseif subEvent == \"UNIT_DIED\" then\n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.GUIDs = nil\n        aura_env.mark = nil\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
