["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 5,\n            expirationTime = GetTime() + 5,\n            assignment = aura_env.assignments[math.random(4)],\n            autoHide = true\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        if aura_env.disabled then return end\n        \n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1249065 then -- Soulfire Convergence\n            aura_env.count = aura_env.count + 1\n            \n            local assignment = aura_env.assignments[aura_env.count]\n            local listDisplay = aura_env.listDisplay[aura_env.count]\n            \n            WeakAuras.ScanEvents(\"LIQUID_SOULFIRE_CONVERGENCE\", destGUID, listDisplay)\n            \n            if destGUID == WeakAuras.myGUID then\n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = 5,\n                    expirationTime = GetTime() + 5,\n                    assignment = assignment,\n                    say = aura_env.say[aura_env.count],\n                    autoHide = true\n                }\n                \n                SendChatMessage(\"Soulfire Convergence: \" .. listDisplay:lower(), \"RAID\")\n                \n                if aura_env.config.tts then\n                    LiquidWeakAuras:PlaySound(assignment)\n                end\n                \n                return true\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1225616 then -- Soulfire Convergence\n   