["custom"] = "aura_env.spellID = 1227276\naura_env.duration = 6\n\n-- This shows in the player's screen\naura_env.assignments = {\n    \"Break left\",\n    \"Break right\"\n}\n\n-- /say chat spam\n-- Leave table empty if no say spam\naura_env.say = {\n    \"Left\",\n    \"Right\"\n}\n\n-- Played if TTS custom option is enabled\n-- Leave table empty if no TTS\naura_env.tts = {\n    \"break left\",\n    \"break right\"\n}\n\n-- How the assignment is displayed on raid leader lists\n-- This is used in the raid message too (so don't omit even if there's no list)\naura_env.listDisplay = {\n    \"Left\",\n    \"Right\"\n}\n\n-- Broadcast with GUID and listDisplay as arguments\n-- Leave as nil if no broadcast\naura_env.broadcastEvent = \"LIQUID_SOULFIRE_CONVERGENCE\"\n\n\n-- listDisplay is appended to this, then sent in /raid\n-- Leave as nil if no raid message\naura_env.raidMessage = \"Soulfray Annihilation: \" \n\n",
