["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = aura_env.duration - 0.2,\n            expirationTime = GetTime() + aura_env.duration - 0.2,\n            assignment = aura_env.assignments[math.random(#aura_env.assignments)],\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        if aura_env.disabled then return end\n        \n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        LiquidWeakAuras:SortTable(aura_env.affected)\n        \n        for index, GUID in ipairs(aura_env.affected) do\n            local listDisplay = aura_env.listDisplay[index]\n            \n            if aura_env.broadcastEvent then\n                WeakAuras.ScanEvents(aura_env.broadcastEvent, GUID, listDisplay)\n            end\n            \n            if GUID == WeakAuras.myGUID then\n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = aura_env.duration - 0.2,\n                    expirationTime = GetTime() + aura_env.duration - 0.2,\n                    assignment = aura_env.assignments[index],\n                    say = aura_env.say[index],\n                    autoHide = true\n                }\n                \n                if aura_env.raidMessage and listDisplay then\n                    SendChatMessage(aura_env.raidMessage .. listDisplay:lower(), \"RAID\")\n                end\n                \n                if aura_env.config.tts then\n                    local tts = aura_env.tts[index]\n                    \n                    if tts then\n                        LiquidWeakAuras:PlaySound(tts)\n                    end\n                end\n            end\n        end\n        \n        aura_env.affected = {}\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == aura_env.spellID then\n            table.insert(aura_env.affected, destGUID)\n            \n            if #aura_env.affected == 1 then\n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        aura_env.affected = {}\n    end\nend",
