["custom"] = "function aura_env:AddSpells()\n    aura_env.spellIDs = {}\n    \n    for spellID, enabled in pairs(aura_env.config) do\n        if enabled then\n            spellID = tonumber(spellID)\n            \n            if spellID then\n                aura_env.spellIDs[spellID] = true\n            end\n        end\n    end\nend\n\nfunction aura_env:BuildStates(allstates, order)\n    for _, state in pairs(allstates) do\n        state.show = false\n        state.changed = true\n    end\n    \n    for index, entry in ipairs(order) do\n        local spellID = entry.spellID\n        \n        if aura_env.spellIDs[spellID] then\n            local expirationTime = entry.expirationTime\n            \n            if expirationTime then\n                allstates[index] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = entry.duration,\n                    expirationTime = entry.expirationTime,\n                    icon = entry.spellIcon,\n                    name = entry.spellName,\n                    GUID = entry.GUID,\n                    index = index\n                }\n            end\n        end\n    end\nend",
["do_custom"] = true,
