["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local order = {}\n        local spellIDs = {\n            372048,\n            368970,\n            358385,\n            108199,\n            179057,\n            202138,\n            207684,\n            119381,\n            116844,\n            102793,\n            102359,\n            192058,\n            30283,\n            109248,\n            46968,\n        }\n        \n        local spellToClass = {\n            [372048] = \"EVOKER\",\n            [368970] = \"EVOKER\",\n            [358385] = \"EVOKER\",\n            [108199] = \"DEATHKNIGHT\",\n            [179057] = \"DEMONHUNTER\",\n            [202138] = \"DEMONHUNTER\",\n            [207684] = \"DEMONHUNTER\",\n            [119381] = \"MONK\",\n            [116844] = \"MONK\",\n            [102793] = \"DRUID\",\n            [102359] = \"DRUID\",\n            [192058] = \"SHAMAN\",\n            [30283]  = \"WARLOCK\",\n            [109248] = \"HUNTER\",\n            [46968]  = \"WARRIOR\",\n        }\n        \n        for _, spellID in ipairs(spellIDs) do\n            local spellInfo = C_Spell.GetSpellInfo(spellID)\n            \n            table.insert(\n                order,\n                {\n                    GUID = UnitGUID(\"player\"),\n                    unit = \"player\",\n                    spellID = spellID,\n                    spellIcon = spellInfo.iconID,\n                    spellName = spellInfo.name,\n                    duration = 60,\n                    expirationTime = math.random(4) == 1 and GetTime() + math.random(60) or GetTime() + 0.1\n                }\n            )\n        end\n        \n        for index, entry in ipairs(order) do\n            local spellID = entry.spellID\n            local class = spellToClass[spellID]\n            local _, _, _, classColor = GetClassColor(class)\n            local optionsName = string.format(\"|c%s%s|r\", classColor, WeakAuras.UnitName(\"player\"))\n            \n            allstates[index] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = entry.duration,\n                expirationTime = entry.expirationTime,\n                icon = entry.spellIcon,\n                name = entry.spellName,\n                index = index,\n                optionsName = optionsName,\n                optionsNameOnCooldown = WeakAuras.UnitName(\"player\"),\n                GUID = entry.GUID\n            }\n        end\n    elseif event == \"LIQUID_SOULBINDER_CC_UPDATE\" then\n        local order = ...\n        \n        aura_env:BuildStates(allstates, order)\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.order = nil\n        \n        aura_env:AddSpells()\n        aura_env:BuildStates(allstates, {})\n        \n        return true\n    end\nend",
