["init"] = {
["custom"] = "-- If anything is added here, it should also be added to the front-end custom options\naura_env.trackedSpells = {\n    [372048] =  1, -- Oppressing Roar\n    [368970] =  2, -- Tail Swipe\n    [108199] =  3, -- Gorefiend's Grasp\n    [179057] =  4, -- Chaos Nova\n    [202138] =  5, -- Sigil of Chains\n    [207684] =  6, -- Sigil of Misery\n    [119381] =  7, -- Leg Sweep\n    [192058] =  8, -- Capacitor Totem\n    [30283]  =  9, -- Shadowfury\n    [46968]  = 10, -- Shockwave\n    [8122]   = 11, -- Psychic Scream\n    [31661]  = 12, -- Dragon's Breath\n    [5246]   = 13, -- Intimidating Shout\n    [207167] = 14, -- Blinding Sleet\n}\n\nlocal LibOpenRaid = LibStub:GetLibrary(\"LibOpenRaid-1.0\", true)\n\n-- Register LibOpenRaid callbacks\nif LibOpenRaid and not aura_env.region.callbacksRegistered then\n    local id = aura_env.id\n    \n    local callbacks = {\n        CooldownListUpdate = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownListUpdate\", id, ...) end,\n        CooldownListWipe = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownListWipe\", id, ...) end,\n        CooldownUpdate = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownUpdate\", id, ...) end,\n        CooldownAdded = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownAdded\", id, ...) end\n    }\n    \n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownListUpdate\", \"CooldownListUpdate\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownListWipe\", \"CooldownListWipe\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownUpdate\", \"CooldownUpdate\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownAdded\", \"CooldownAdded\")\n    \n    aura_env.region.callbacksRegistered = true\nend\n\nlocal function GetEntry(GUID, spellID)\n    for _, entry in ipairs(aura_env.entries) do\n        if entry.GUID == GUID and entry.spellID == spellID then\n            return entry\n        end\n    end\nend\n\nfunction aura_env:UpdateEntry(GUID, spellID, cooldownInfo)\n    if not LibOpenRaid then return false end\n    if not GUID then return end\n    if not cooldownInfo then return false end\n    if not aura_env.trackedSpells[spellID] then return false end\n    \n    local unit = aura_env.GUIDToUnit[GUID]\n    local visible = UnitIsVisible(unit)\n    \n    if not visible then return end -- Do not show entries for units outside the raid\n    \n    local entry = GetEntry(GUID, spellID)\n    \n    -- If no entry for this GUID/spellID combination exists, create it\n    if not entry then\n        local spellInfo = C_Spell.GetSpellInfo(spellID)\n        \n        table.insert(\n            aura_env.entries,\n            {\n                GUID = GUID,\n                spellID = spellID,\n                spellName = spellInfo.name,\n                spellIcon = spellInfo.iconID\n            }\n        )\n        \n        entry = aura_env.entries[#aura_env.entries]\n    end\n    \n    -- Update expirationTime/duration for the entry\n    local _, _, timeLeft, charges, _, _, _, duration = LibOpenRaid.GetCooldownStatusFromCooldownInfo(cooldownInfo)\n    local expirationTime = charges >= 1 and 0 or GetTime() + timeLeft\n    \n    entry.duration = duration\n    entry.expirationTime = expirationTime\nend\n\nfunction aura_env:UpdateAllEntries()\n    if not LibOpenRaid then return end\n    \n    -- Wipe all info\n    aura_env.entries = {}\n    \n    -- Update entries\n    local allUnitsCooldown = LibOpenRaid.GetAllUnitsCooldown()\n    \n    if allUnitsCooldown then\n        for unit, unitCooldowns in pairs(allUnitsCooldown) do\n            local GUID = UnitGUID(unit)\n            \n            for spellID, cooldownInfo in pairs(unitCooldowns) do\n                aura_env:UpdateEntry(GUID, spellID, cooldownInfo)\n            end\n        end\n    end\n    \n    -- Sort entries\n    table.sort(\n        aura_env.entries,\n        function(entryA, entryB)\n            local spellA = entryA.spellID\n            local spellB = entryB.spellID\n            \n            local indexA = aura_env.trackedSpells[spellA]\n            local indexB = aura_env.trackedSpells[spellB]\n            \n            if indexA ~= indexB then\n                return indexA < indexB\n            end\n            \n            return entryA.GUID < entryB.GUID\n        end\n    )\nend\n\nfunction aura_env:AddNoteSpells()\n    local spellCount = 0\n    \n    for _ in pairs(aura_env.trackedSpells) do\n        spellCount = spellCount + 1\n    end\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"CC\") do\n        local _, spellID = line:match(\"^.-%s+(%d+)\")\n        \n        spellID = tonumber(spellID)\n        \n        if spellID and not aura_env.trackedSpells[spellID] then\n            spellCount = spellCount + 1\n            \n            aura_env.trackedSpells[spellID] = spellCount\n        end\n    end\nend\n\nfunction aura_env:Notify()\n    WeakAuras.ScanEvents(\"LIQUID_SOULBINDER_CC_UPDATE\", aura_env.entries)\nend",
