["custom"] = "function(event, ...)\n    if event == \"LIQUID_CooldownListUpdate\" then\n        local id, unit, unitCooldowns = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local GUID = UnitGUID(unit)\n        \n        if unitCooldowns then\n            for spellID, cooldownInfo in pairs(unitCooldowns) do\n                aura_env:UpdateEntry(GUID, spellID, cooldownInfo)\n            end\n        end\n        \n        aura_env:Notify()\n    elseif event == \"LIQUID_CooldownListWipe\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env:UpdateAllEntries()\n        aura_env:Notify()\n    elseif event == \"LIQUID_CooldownUpdate\" or event == \"LIQUID_CooldownAdded\" then\n        local id, unit, spellID, cooldownInfo = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local GUID = UnitGUID(unit)\n        \n        aura_env:UpdateEntry(GUID, spellID, cooldownInfo)\n        aura_env:Notify()\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env:AddNoteSpells()\n        aura_env:UpdateAllEntries()\n        aura_env:Notify()\n    end\nend",
