["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 7,\n            expirationTime = GetTime() + 7,\n            additionalProgress ={\n                {\n                    min = 0,\n                    max = 1\n                }\n            },\n            lastSet = true,\n            autoHide = true\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1233979 then -- Astral Harvest\n            -- Increase set count (only for the first application per set)\n            local currentTime = GetTime()\n            local timeSinceLastApplication = currentTime - aura_env.lastApplicationTime\n            \n            if timeSinceLastApplication > 3 then\n                aura_env.set = aura_env.set + 1\n            end\n            \n            aura_env.lastApplicationTime = currentTime\n            \n            -- If we are affected, create states\n            if destGUID == WeakAuras.myGUID  then\n                -- The 5th/9th set is a bit special, since silence circles are applied after the add sets\n                -- But they're applied early enough that if you instantly stack up, they drop where you want to spawn adds\n                -- Just tell everyone to stay out until the last 2 seconds\n                if aura_env.set == 5 or aura_env.set == 9 then\n                    allstates[\"\"] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = 7,\n                        expirationTime = currentTime + 7,\n                        additionalProgress ={\n                            {\n                                min = 0,\n                                max = 1\n                            }\n                        },\n                        lastSet = true,\n                        overlap = false,\n                        autoHide = true\n                    }\n                else\n                    local auraInfo = C_UnitAuras.GetPlayerAuraBySpellID(1228188) or C_UnitAuras.GetPlayerAuraBySpellID(1238874) -- Silencing/Echoing Tempest\n                    \n                    if auraInfo then\n                        local expirationTime = auraInfo.expirationTime\n                        local remainingTime = expirationTime - currentTime\n                        \n                        allstates[\"\"] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            duration = 7,\n                            expirationTime = currentTime + 7,\n                            additionalProgress ={\n                                {\n                                    min = 0,\n                                    max = 7 - remainingTime\n                                }\n                            },\n                            overlap = true, -- Whether this one overlaps with Silencing/Echoing Tempest\n                            lastSet = false,\n                            autoHide = true\n                        }\n                    else\n                        allstates[\"\"] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            duration = 7,\n                            expirationTime = currentTime + 7,\n                            overlap = false,\n                            lastSet = false,\n                            autoHide = true\n                        }\n                    end\n                end\n                \n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.set = 0\n        aura_env.lastApplicationTime = 0\n    end\nend",
