["names"] = {
},
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local GUID = UnitGUID(\"target\")\n        \n        if not GUID then return end\n        \n        local nextCast = aura_env.GUIDToNextCast[GUID]\n        \n        if nextCast and nextCast ~= 0 then\n            local timeToNextCast = nextCast - GetTime()\n            \n            if timeToNextCast > 4.2 then return end\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 19,\n                expirationTime = nextCast,\n                autoHide = true\n            }\n            \n            return true\n        end\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local unit, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1227631 then -- Arcane Expulsion\n            for GUID in pairs(aura_env.GUIDToCastCount) do\n                aura_env.GUIDToCastCount[GUID] = 0\n            end\n            \n            for GUID in pairs(aura_env.GUIDToNextCast) do\n                aura_env.GUIDToNextCast[GUID] = 0\n            end\n        end\n        \n        local GUID = UnitGUID(unit)\n        \n        if aura_env.GUIDToCastCount[GUID] and spellID == 1234328 then -- Photon Blast\n            aura_env.GUIDToCastCount[GUID] = aura_env.GUIDToCastCount[GUID] + 1\n            \n            if aura_env.GUIDToCastCount[GUID] < 4 then\n                aura_env.GUIDToNextCast[GUID] = GetTime() + 4\n            else\n                local id = aura_env.id\n                \n                aura_env.GUIDToNextCast[GUID] = GetTime() + 19\n                aura_env.GUIDToCastCount[GUID] = 0\n                \n                C_Timer.After(15, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id) end)\n            end\n            \n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        end\n    elseif event == \"UNIT_POWER_UPDATE\" then\n        local unit = ...\n        local power = UnitPower(unit)\n        \n        if power == 0 then return end\n        \n        local GUID = UnitGUID(unit)\n        local nextCast = aura_env.GUIDToNextCast[GUID]\n        \n        -- If nextCast is equal to 0, the Arcane Collector has never cast Photon Blast yet\n        -- In other words, this is the first time the Arcane Collector gains energy\n        if nextCast == 0 then\n            aura_env.GUIDToNextCast[GUID] = GetTime() + 3\n            \n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        end\n    elseif event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" then\n        for i = 1, 12 do\n            local unit = \"boss\" .. i\n            \n            if UnitExists(unit) then\n                local GUID = UnitGUID(unit)\n                local newGUID = not aura_env.GUIDToNextCast[GUID]\n                \n                local npcID = select(6, strsplit(\"-\", GUID))\n                local isArcaneCollector = npcID == \"240905\"\n                \n                if newGUID and isArcaneCollector then\n                    aura_env.GUIDToCastCount[GUID] = 0\n                    aura_env.GUIDToNextCast[GUID] = 0\n                end\n            end\n        end\n    elseif event == \"PLAYER_TARGET_CHANGED\" then\n        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.GUIDToCastCount = {} -- Arcane Collector GUID to number of Photon Blast casts\n        aura_env.GUIDToNextCast = {} -- Arcane Collector GUID to timestamp of their next Photon Blast cast success\n    end\nend",
