["custom"] = "if aura_env.config.tts then\n    local set = aura_env.state.set\n    local spellID = aura_env.state.spellID\n    local ttsPlayed = set and spellID and aura_env.ttsPlayed[set][spellID]\n    \n    if not ttsPlayed then\n        LiquidWeakAuras:PlaySound(\"next\")\n    end\n    \n    if set and spellID then\n        aura_env.ttsPlayed[set][spellID] = true\n    end\nend",
