["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local spellIDs = {\n            372048,\n            368970,\n            179057,\n            119381,\n            192058\n        }\n        \n        local spellID = spellIDs[math.random(#spellIDs)]\n        local spellInfo = C_Spell.GetSpellInfo(spellID)\n        local iconID = spellInfo.iconID\n        local icon = CreateTextureMarkup(iconID, 64, 64, 0, 0, 5/64, 59/64, 5/64, 59/64)\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            icon = icon\n        }\n    elseif event == \"LIQUID_NEXT_CC\" then\n        local GUID, spellID, set = ...\n        \n        aura_env.set = set\n        \n        if not aura_env.ttsPlayed[set] then aura_env.ttsPlayed[set] = {} end\n        \n        if GUID == WeakAuras.myGUID then\n            if aura_env.finishedCasts[set] and aura_env.finishedCasts[set][spellID] then return end -- We already cast this spell\n            \n            local spellInfo = C_Spell.GetSpellInfo(spellID)\n            local iconID = spellInfo.iconID\n            local icon = CreateTextureMarkup(iconID, 64, 64, 0, 0, 5/64, 59/64, 5/64, 59/64)\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                spellID = spellID,\n                icon = icon\n            }\n            \n            return true\n        else\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if not aura_env.set then return end\n        \n        if not aura_env.finishedCasts[aura_env.set] then aura_env.finishedCasts[aura_env.set] = {} en