["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local differencePercentage = math.random() * 40 - 20\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            percentage = differencePercentage,\n            averageHealthFraction = 1\n        }\n    elseif event == \"FRAME_UPDATE\" then\n        -- Clear states\n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        \n        -- Determine average/lowest health\n        local bossStates = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 2)\n        \n        if not bossStates then return true end\n        \n        local collectorCount = 0\n        local averageHealth = 0\n        local averageHealthFraction, totalHealth\n        \n        for _, state in pairs(bossStates) do\n            collectorCount = collectorCount + 1\n            averageHealth = averageHealth + state.value\n            totalHealth = state.total\n        end\n        \n        if collectorCount == 0 then return true end\n        \n        averageHealth = averageHealth / collectorCount\n        averageHealthFraction = averageHealth / totalHealth\n        \n        -- Create states\n        local nameplateStates = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 3)\n        \n        if not nameplateStates then return true end\n        \n        for unit, state in pairs(nameplateStates) do\n            local currentHealth = state.value\n            \n            local differenceFromAverage = currentHealth - averageHealth\n            local differencePercentage = (differenceFromAverage / totalHealth) * 100\n            \n            allstates[unit] = {\n                show = true,\n                changed = true,\n                unit = unit,\n                percentage = differencePercentage,\n                averageHealthFraction = averageHealthFraction\n            }\n        end\n        \n        return true\n    elseif event == \"NAME_PLATE_UNIT_REMOVED\" then\n        local unit = ...\n        \n        if not unit then return end\n        \n        local state = allstates[unit]\n        \n        if state then\n            state.show = false\n            state.changed = true\n            \n            return true\n        end\n    end\nend",
