["customGrow"] = "function(newPositions, activeRegions)\n    local SPACING = 3\n    \n    local xMin = math.huge\n    local xMax = -math.huge\n    \n    -- Calculate total width\n    local totalWidth = (#activeRegions - 1) * SPACING\n    \n    for _, regionData in ipairs(activeRegions) do\n        local region = regionData.region\n        \n        totalWidth = totalWidth + region.width\n    end\n    \n    local x = math.floor(-(totalWidth / 2))\n    \n    -- Position auras\n    for i, regionData in ipairs(activeRegions) do\n        local region = regionData.region\n        \n        x = x + 0.5 * region.width\n        \n        xMin = math.min(xMin, x)\n        xMax = math.max(xMax, x)\n        \n        newPositions[i] = {x, 0}\n        \n        x = x + 0.5 * region.width + SPACING\n    end\n    \n    -- Center the graphic\n    local xOffset = math.floor(0.5 * (xMax - math.abs(xMin)))\n    \n    for _, position in ipairs(newPositions) do\n        position[1] = position[1] - xOffset\n    end\nend",
