["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local order = {}\n        local spellIDs = {\n            372048,\n            368970,\n            109248,\n            108199,\n            46968,\n            116844,\n        }\n        \n        local spellToClass = {\n            [372048] = \"EVOKER\",\n            [368970] = \"EVOKER\",\n            [358385] = \"EVOKER\",\n            [108199] = \"DEATHKNIGHT\",\n            [179057] = \"DEMONHUNTER\",\n            [202138] = \"DEMONHUNTER\",\n            [207684] = \"DEMONHUNTER\",\n            [119381] = \"MONK\",\n            [116844] = \"MONK\",\n            [102793] = \"DRUID\",\n            [102359] = \"DRUID\",\n            [192058] = \"SHAMAN\",\n            [30283]  = \"WARLOCK\",\n            [109248] = \"HUNTER\",\n            [46968]  = \"WARRIOR\",\n        }\n        \n        for _, spellID in ipairs(spellIDs) do\n            local spellInfo = C_Spell.GetSpellInfo(spellID)\n            \n            table.insert(\n                order,\n                {\n                    GUID = UnitGUID(\"player\"),\n                    spellID = spellID,\n                    icon = spellInfo.iconID,\n                }\n            )\n        end\n        \n        local me = math.random(6)\n        \n        for index, entry in ipairs(order) do\n            local spellID = entry.spellID\n            local class = spellToClass[spellID]\n            local _, _, _, classColor = GetClassColor(class)\n            local optionsName = string.format(\"|c%s%s|r\", classColor, WeakAuras.UnitName(\"player\"))\n            \n            allstates[index] = {\n                show = true,\n                changed = true,\n                icon = entry.icon,                \n                index = index,\n                optionsName = optionsName,\n                me = me == index,\n                GUID = entry.GUID\n            }\n            \n            if index == 3 then\n                allstates[index].progressType = \"timed\"\n                allstates[index].duration = 45\n                allstates[index].expirationTime = GetTime() + 10\n            end\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, _, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and (spellID == 1233979 or spellID == 1243873) then -- Astral Harvest/Void Harvest\n            local currentTime = GetTime()\n            local timeSinceLastSetIncrease = currentTime - (aura_env.lastSetIncrease or 0)\n            \n            if timeSinceLastSetIncrease > 3 then\n                aura_env.set = aura_env.set + 1\n                aura_env.lastSetIncrease = currentTime\n                \n                aura_env:BuildStates(allstates)\n                aura_env:NotifyNext()\n                \n                return true\n            end\n        end\n    elseif event == \"LIQUID_CC_UPDATE\" then\n        local receivedInfo = ...\n        \n        if not receivedInfo then return end\n        \n        aura_env:UpdateCooldownInfo(receivedInfo)\n        aura_env:BuildStates(allstates)\n        aura_env:NotifyNext()\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.set = 0\n        \n        aura_env:ReadNote()\n    end\nend",
