["init"] = {
["custom"] = "function aura_env:ReadNote()\n    aura_env.GUIDToHealerGUID = {} -- Maps player GUIDs to their preferred healer GUID\n    aura_env.backupHealerGUIDs = {} -- Sorted list of backup healers. Non-hard assigned healers have higher priority.\n    \n    local assignedHealerGUIDs = {}\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"Ire\") do\n        local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n        \n        if next(GUIDs) then\n            local healerGUID\n            \n            -- First find the healer GUID\n            for _, GUID in ipairs(GUIDs) do\n                local unit = aura_env.GUIDToUnit[GUID]\n                local isHealer = UnitGroupRolesAssigned(unit) == \"HEALER\"\n                \n                if isHealer then\n                    healerGUID = GUID\n                    \n                    tInsertUnique(assignedHealerGUIDs, healerGUID)\n                    \n                    break\n                end\n            end\n            \n            -- Assign each GUID to be dispelled by the found healer\n            if healerGUID then\n                for _, GUID in pairs(GUIDs) do\n                    aura_env.GUIDToHealerGUID[GUID] = healerGUID\n                end\n            end\n        end\n    end\n    \n    -- Find unassigned healer GUIDs, they are first prio in the backup list\n    for unit in WA_IterateGroupMembers() do\n        local GUID = UnitGUID(unit)\n        local isVisible = UnitIsVisible(unit)\n        local isHealer = UnitGroupRolesAssigned(unit) == \"HEALER\"\n        local isAssigned = tContains(aura_env.GUIDToHealerGUID, GUID)\n        \n        if isVisible and isHealer and not isAssigned then\n            table.insert(aura_env.backupHealerGUIDs, GUID)\n        end\n    end\n    \n    table.sort(aura_env.backupHealerGUIDs)\n    table.sort(assignedHealerGUIDs)\n    \n    tAppendAll(aura_env.backupHealerGUIDs, assignedHealerGUIDs)\nend\n\nfunction aura_env:AssignHealer(allstates, GUID, healerGUID)\n    aura_env.lastDispelAssignmentTime[healerGUID] = GetTime()\n    \n    if healerGUID == WeakAuras.myGUID then\n        local targetUnit = aura_env.GUIDToUnit[GUID]\n        local targetName = UnitName(targetUnit)\n        \n        -- Hide regular state for target\n        local state = allstates[GUID]\n        \n        if state then\n            state.show = false\n            state.changed = true\n        end\n        \n        -- Create a green glow state for target\n        allstates[GUID .. \"dispel\"] = {\n            show = true,\n            changed = true,\n            macroPressed = true,\n            GUID = GUID,\n            unit = targetUnit\n        }\n        \n        SendChatMessage(string.format(\"I'm assigned to dispel %s\", targetName), \"RAID\")\n    end\n    \n    WeakAuras.ScanEvents(\"LIQUID_DEVOURERS_IRE_ASSIGNMENT\", GUID, healerGUID)\nend",
