["custom"] = "function(allstates, event, ...)\n    if event == \"LIQUID_PRIVATE_AURA_MACRO\" then\n        if C_UnitAuras.GetPlayerAuraBySpellID(1222232) and not aura_env.macroPressed then -- Devourer's Ire\n            aura_env.macroPressed = true\n            \n            SendChatMessage(\"Devourer's Ire macro pressed!\", \"RAID\")\n            \n            LiquidWeakAuras:Broadcast(\"LIQUID_DISPEL_EVENT\", 1222232, WeakAuras.myGUID) -- Devourer's Ire\n        end\n    elseif event == \"LIQUID_DISPEL_EVENT\" then\n        local spellID, GUID = ...\n        \n        if spellID ~= 1222232 then return end -- Devourer's Ire\n        \n        local assignedHealerGUID = aura_env.GUIDToHealerGUID[GUID]\n        \n        -- If the player that pressed their macro has a healer assigned to them, assign them if possible\n        if assignedHealerGUID then\n            local assignedHealerUnit = aura_env.GUIDToUnit[assignedHealerGUID]\n            local isAlive = not UnitIsDeadOrGhost(assignedHealerUnit)\n            \n            if isAlive then\n                aura_env:AssignHealer(allstates, GUID, assignedHealerGUID)\n                \n                return true\n            end\n        end\n        \n        -- If the assigned healer couldn't be chosen (because they are dead), choose a backup\n        -- First try to select a backup that hasn't been assigned a dispel in the past 8 seconds\n        -- and that is not affected themselves\n        for _, healerGUID in ipairs(aura_env.backupHealerGUIDs) do\n            local healerUnit = aura_env.GUIDToUnit[healerGUID]\n            local isAlive = not UnitIsDeadOrGhost(healerUnit)\n            local lastDispelAssignmentTime = aura_env.lastDispelAssignmentTime[healerGUID] or 0\n            local isAffected = WA_GetUnitDebuff(healerUnit, 1222232) -- Devourer's Ire\n            \n            if isAlive and not isAffected and lastDispelAssignmentTime < GetTime() - 8 then\n                aura_env:AssignHealer(allstates, GUID, healerGUID)\n                \n                return true\n            end\n        end\n        \n        -- Assign the highest priority backup healer (first in the array) that is alive/not affected\n        -- Basically the same as above, but don't check for last dispel assignment time\n        -- This should practically ever happen\n        for _, healerGUID in ipairs(aura_env.backupHealerGUIDs) do\n            local healerUnit = aura_env.GUIDToUnit[healerGUID]\n            local isAlive = not UnitIsDeadOrGhost(healerUnit)\n            local isAffected = WA_GetUnitDebuff(healerUnit, 1222232) -- Devourer's Ire\n            \n            if isAlive and not isAffected then\n                aura_env:AssignHealer(allstates, GUID, healerGUID)\n                \n                return true\n            end\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1222232 then -- Devourer's Ire\n            if destGUID == WeakAuras.myGUID then\n                aura_env.macroPressed = false\n                \n                return\n            end\n            \n            -- If we are the assigned healer to dispel the target, glow their frame red\n            local assignedHealerGUID = aura_env.GUIDToHealerGUID[destGUID]\n            \n            if assignedHealerGUID == WeakAuras.myGUID then\n                allstates[destGUID] = {\n                    show = true,\n                    changed = true,\n                    macroPressed = false,\n                    GUID = destGUID,\n                    unit = aura_env.GUIDToUnit[destGUID]\n                }\n            end\n            \n            return true\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellID == 1222232 then -- Devourer's Ire\n            for _, state in pairs(allstates) do\n                if state.GUID == destGUID then\n                    state.show = false\n                    state.changed = true\n                    \n                    return true\n                end\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.macroPressed = false\n        aura_env.lastDispelAssignmentTime = {} -- Healer GUIDs -> time they last got assigned to dispel someone\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n        \n        aura_env:ReadNote()\n    end\nend",
