["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 32,\n            expirationTime = GetTime() + 32,\n            assignment = string.format(\"{rt%d}\", math.random(7)),\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        if aura_env.disabled then return end\n        \n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        -- Sort all subgroups\n        for _, group in pairs(aura_env.affected) do\n            table.sort(group)\n        end\n        \n        -- Sort groups\n        -- Warlocks and Shadow Priests should be close to star\n        table.sort(\n            aura_env.affected,\n            function(groupA, groupB)\n                if #groupA == 0 then return false end\n                if #groupB == 0 then return true end\n                \n                -- Count warlocks/shadow priests in group A\n                local countA = 0\n                \n                for _, GUID in pairs(groupA) do\n                    local isWarlockOrShadowPriest = aura_env.isWarlockOrShadowPriest[GUID]\n                    \n                    if isWarlockOrShadowPriest then\n                        countA = countA + 1\n                    end\n                end\n                \n                -- Count warlocks/shadow priests in group A\n                local countB = 0\n                \n                for _, GUID in pairs(groupB) do\n                    local isWarlockOrShadowPriest = aura_env.isWarlockOrShadowPriest[GUID]\n                    \n                    if isWarlockOrShadowPriest then\n                        countB = countB + 1\n                    end\n                end\n                \n                if countA ~= countB then\n                    return countA > countB\n                end\n                \n                -- Count healers in group A\n                local healerCountA = 0\n                \n                for _, GUID in pairs(groupA) do\n                    local isHealer = aura_env.isHealer[GUID]\n                    \n                    if isHealer then\n                        healerCountA = healerCountA + 1\n                    end\n                end\n                \n                -- Count healers in group B\n                local healerCountB = 0\n                \n                for _, GUID in pairs(groupB) do\n                    local isHealer = aura_env.isHealer[GUID]\n                    \n                    if isHealer then\n                        healerCountB = healerCountB + 1\n                    end\n                end\n                \n                if healerCountA ~= healerCountB then\n                    return healerCountA > healerCountB\n                end\n                \n                return groupA[1] < groupB[1]\n            end\n        )\n        \n        for groupIndex, group in pairs(aura_env.affected) do\n            if tContains(group, WeakAuras.myGUID) then\n                local markIndex = aura_env.indexToMark[groupIndex]\n                local mark = string.format(\"{rt%d}\", markIndex)\n                \n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = aura_env.config.duration,\n                    expirationTime = GetTime() + aura_env.config.duration,\n                    assignment = mark,\n                    autoHide = true\n                }\n                \n                if aura_env.config.tts then\n                    LiquidWeakAuras:PlaySound(mark)\n                end\n            end\n        end\n        \n        aura_env.timeSinceAssignment = GetTime()\n        aura_env.intermissionCount = aura_env.intermissionCount + 1\n        aura_env.affectedCount = 0\n        aura_env.affected = {{}, {}, {}, {}, {}, {}, {}}\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        if GetTime() < aura_env.timeSinceAssignment + 60 then return end\n        if aura_env.intermissionCount >= 2 then return end -- Only show assignments in first two intermissions\n        \n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1242883 then -- Soul Tether\n            aura_env.affectedCount = aura_env.affectedCount + 1\n            \n            local groupIndex = math.floor((aura_env.affectedCount - 1) / 3) + 1\n            \n            table.insert(aura_env.affected[groupIndex], destGUID)\n            \n            if aura_env.affectedCount == 1 then\n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"STATUS\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        \n        aura_env.timeSinceAssignment = 0\n        aura_env.intermissionCount = 0\n        aura_env.affectedCount = 0\n        aura_env.affected = {{}, {}, {}, {}, {}, {}, {}}\n        aura_env.isWarlockOrShadowPriest = {}\n        aura_env.isHealer = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            local role = UnitGroupRolesAssigned(unit)\n            local class = UnitClassBase(unit)\n            \n            if class == \"WARLOCK\" or (class == \"PRIEST\" and role == \"DAMAGER\") then\n                aura_env.isWarlockOrShadowPriest[GUID] = true\n            end\n            \n            if role == \"HEALER\" then\n                aura_env.isHealer[GUID] = true\n            end\n        end\n    end\nend",
