["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6.3,\n            expirationTime = GetTime() + 6.3,\n            assignment = math.random(3),\n            autoHide = true\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            if spellID == 1227847 then -- The Hunt\n                aura_env.count = aura_env.count + 1\n                \n                if aura_env.count == 4 then\n                    aura_env.count = 1\n                end\n                \n                if aura_env.count == aura_env.assignment then\n                    allstates[destGUID] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = 6.3,\n                        expirationTime = GetTime() + 6.3,\n                        assignment = aura_env.count,\n                        autoHide = true\n                    }\n                    \n                    if aura_env.config.tts then\n                        LiquidWeakAuras:PlaySound(\"soak\")\n                    end\n                    \n                    return true\n                end\n            elseif aura_env.immuneSpellIDs[spellID] then\n                local state = allstates[destGUID]\n                \n                if state then\n                    state.show = false\n                    state.changed = true\n                    \n                    return true\n                end\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 0\n        \n        aura_env:ReadNote()\n    end\nend",
