["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            count = math.random(40),\n            total = 40\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" then\n            if spellID == 1233093 then -- Collapsing Star (aura applied to boss)\n                local total = 0 -- Total number of soaks\n                \n                for unit in WA_IterateGroupMembers() do\n                    local isAlive = not UnitIsDeadOrGhost(unit)\n                    local isVisible = UnitIsVisible(unit)\n                    \n                    if isAlive and isVisible then\n                        total = total + aura_env.soaksPerPlayer\n                    end\n                end\n                \n                total = math.ceil(total)\n                \n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    count = total,\n                    total = total\n                }\n                \n                aura_env.maxHits = 0\n                aura_env.hits = {}\n                \n                return true\n            end\n        elseif subEvent == \"SPELL_DAMAGE\" or subEvent == \"SPELL_MISSED\" then\n            if spellID == 1233104 then -- Collapsing Star (fragment going in)\n                local hits = (aura_env.hits[destGUID] or 0) + 1\n                \n                aura_env.hits[destGUID] = hits\n                \n                if hits > aura_env.maxHits then\n                    aura_env.maxHits = hits\n                    \n                    if state then\n                        state.count = state.count - 1\n                        state.changed = true\n                        state.show = state.count > 0\n                        \n                        return true\n                    end\n                end\n            elseif spellID == 1233103 then -- Collapsing Star (soaking star)\n                local state = allstates[\"\"]\n                \n                if state then\n                    state.count = state.count - 1\n                    state.changed = true\n                    state.show = state.count > 0\n                    \n                    return true\n                end\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellID == 1233093 then -- Collapsing Star\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.soaksPerPlayer = difficultyID == 16 and 2.5 or 2\n        aura_env.hits = {} -- GUID to number of hits of fragments going in\n        aura_env.maxHits = 0\n    end\nend",
