["customGrow"] = "function(newPositions, activeRegions)\n    -- If options are open, just do a centered horizontal grow\n    if WeakAuras.IsOptionsOpen() then\n        local SPACING = 3\n        \n        local xMin = math.huge\n        local xMax = -math.huge\n        \n        -- Calculate total width\n        local totalWidth = (#activeRegions - 1) * SPACING\n        \n        for _, regionData in ipairs(activeRegions) do\n            local region = regionData.region\n            \n            totalWidth = totalWidth + region.width\n        end\n        \n        local x = math.floor(-(totalWidth / 2))\n        \n        -- Position auras\n        for i, regionData in ipairs(activeRegions) do\n            local region = regionData.region\n            \n            x = x + 0.5 * region.width\n            \n            xMin = math.min(xMin, x)\n            xMax = math.max(xMax, x)\n            \n            newPositions[i] = {x, 0}\n            \n            x = x + 0.5 * region.width + SPACING\n        end\n        \n        -- Center the graphic\n        local xOffset = math.floor(0.5 * (xMax - math.abs(xMin)))\n        \n        for _, position in ipairs(newPositions) do\n            position[1] = position[1] - xOffset\n        end\n        \n        return\n    end\n    \n    local SPACING = 3\n    \n    local frameToX = {} -- Anchor frame to X\n    \n    -- Position auras\n    for _, regionData in ipairs(activeRegions) do\n        local region = regionData.region\n        local state = region.state\n        local anchorFrame = state.anchorFrame\n        \n        if anchorFrame then\n            if not newPositions[anchorFrame] then newPositions[anchorFrame] = {} end\n            if not frameToX[anchorFrame] then frameToX[anchorFrame] = 0.5 * anchorFrame:GetWidth() + SPACING end\n            \n            local x = frameToX[anchorFrame]\n            local anchorRegionHeight = anchorFrame:GetHeight()\n            \n            region:SetRegionWidth(anchorRegionHeight)\n            region:SetRegionHeight(anchorRegionHeight)\n            \n            newPositions[anchorFrame][regionData] = {x, 0, true}\n            \n            frameToX[anchorFrame] = frameToX[anchorFrame] + anchorRegionHeight + SPACING\n        end\n    end\nend",
