["do_custom"] = true,
["custom"] = "local LibOpenRaid = LibStub:GetLibrary(\"LibOpenRaid-1.0\", true)\n\nlocal trackedSpellIDs = {\n    [45438] = true, -- Ice Block\n    [196555] = true, -- Netherwalk\n    [186265] = true, -- Turtle\n    [642] = true, -- Divine Shield\n    [47585] = true, -- Dispersion\n    [31224] = true, -- Cloak of Shadows\n}\n\n-- Register LibOpenRaid callbacks\nif LibOpenRaid and not aura_env.region.callbacksRegistered then\n    local id = aura_env.id\n    \n    local callbacks = {\n        CooldownListUpdate = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownListUpdate\", id, ...) end,\n        CooldownListWipe = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownListWipe\", id, ...) end,\n        CooldownUpdate = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownUpdate\", id, ...) end,\n        CooldownAdded = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownAdded\", id, ...) end\n    }\n    \n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownListUpdate\", \"CooldownListUpdate\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownListWipe\", \"CooldownListWipe\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownUpdate\", \"CooldownUpdate\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownAdded\", \"CooldownAdded\")\n    \n    aura_env.region.callbacksRegistered = true\nend\n\nfunction aura_env:UpdateCooldownInfo(allstates, GUID, spellID, receivedInfo)\n    if not LibOpenRaid then return end\n    if not receivedInfo then return end\n    if not trackedSpellIDs[spellID] then return end\n    \n    if not aura_env.cooldownInfo then return end\n    if not aura_env.cooldownInfo[GUID] then aura_env.cooldownInfo[GUID] = {} end\n    if not aura_env.cooldownInfo[GUID][spellID] then aura_env.cooldownInfo[GUID][spellID] = {} end\n    \n    local _, _, timeLeft, charges, _, _, _, duration = LibOpenRaid.GetCooldownStatusFromCooldownInfo(receivedInfo)\n    local expirationTime = charges >= 1 and 0 or GetTime() + timeLeft\n    \n    -- Update info\n    local cooldownInfo = aura_env.cooldownInfo[GUID][spellID]\n    \n    cooldownInfo.duration = duration or 0\n    cooldownInfo.expirationTime = expirationTime or 0\n    \n    -- Update states if any exist\n    return aura_env:UpdateStates(allstates, GUID, spellID)\nend\n\nfunction aura_env:UpdateAllCooldownInfo(allstates)\n    if not LibOpenRaid then return end\n    \n    aura_env.cooldownInfo = {}\n    \n    local allUnitsCooldown = LibOpenRaid.GetAllUnitsCooldown()\n    \n    if allUnitsCooldown then\n        for unit, unitCooldowns in pairs(allUnitsCooldown) do\n            local GUID = UnitGUID(unit)\n            \n            if GUID then\n                for spellID, cooldownInfo in pairs(unitCooldowns) do\n                    aura_env:UpdateCooldownInfo(allstates, GUID, spellID, cooldownInfo)\n                end\n            end\n        end\n    end\n    \n    LibOpenRaid.RequestAllData()\nend\n\nfunction aura_env:ShowOptionsStates(allstates)\n    if not LibOpenRaid then return end\n    \n    for spellID in pairs(trackedSpellIDs) do\n        local isOnCooldown = math.random(4) == 1\n        \n        if isOnCooldown then\n            allstates[spellID] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 60,\n                expirationTime = GetTime() + 20 + math.random(40),\n                spellID = spellID,\n                index = spellID,\n                GUID = UnitGUID(\"player\"),\n                affected = false,\n                icon = C_Spell.GetSpellTexture(spellID),\n                autoHide = false\n            }\n        else\n            allstates[spellID] = {\n                show = true,\n                changed = true,\n                spellID = spellID,\n                index = spellID,\n                GUID = UnitGUID(\"player\"),\n                affected = false,\n                icon = C_Spell.GetSpellTexture(spellID),\n                autoHide = false\n            }\n        end\n    end\nend\n\n-- Shows states on some specific anchor\nfunction aura_env:ShowStates(allstates, GUID, anchorFrame)\n    if not LibOpenRaid then return end\n    if not aura_env.cooldownInfo then return end\n    \n    local cooldownInfo = aura_env.cooldownInfo[GUID]\n    \n    if not cooldownInfo then return end\n    \n    local unit = aura_env.GUIDToUnit[GUID]\n    \n    for spellID, info in pairs(cooldownInfo) do\n        local stateKey = WeakAuras.GenerateUniqueID()\n        local _, _, _, _, duration, expirationTime = WA_GetUnitBuff(unit, spellID)\n        \n        allstates[stateKey] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration or info.duration,\n            expirationTime = expirationTime or info.expirationTime,\n            spellID = spellID,\n            index = spellID,\n            GUID = GUID,\n            affected = duration ~= nil,\n            icon = C_Spell.GetSpellTexture(spellID),\n            anchorFrame = anchorFrame,\n            autoHide = false\n        }\n    end\nend\n\n-- Hides states attached to some specific anchor\nfunction aura_env:HideStates(allstates, GUID, anchorFrame)\n    if not LibOpenRaid then return end\n    \n    for _, state in pairs(allstates) do\n        if state.GUID == GUID and state.anchorFrame == anchorFrame then\n            state.show = false\n            state.changed = true\n        end\n    end\nend\n\n-- Updates existing states for a GUID/spellID combination, across all anchors\nfunction aura_env:UpdateStates(allstates, GUID, spellID)\n    if not LibOpenRaid then return end\n    if not trackedSpellIDs[spellID] then return end\n    \n    local cooldownInfo = aura_env.cooldownInfo[GUID] and aura_env.cooldownInfo[GUID][spellID]\n    \n    if not cooldownInfo then return end\n    \n    local changed = false\n    local unit = aura_env.GUIDToUnit[GUID]\n    local _, _, _, _, duration, expirationTime = WA_GetUnitBuff(unit, spellID)\n    \n    for _, state in pairs(allstates) do\n        if state.GUID == GUID and state.spellID == spellID then\n            state.changed = true\n            \n            if duration then\n                state.duration = duration\n                state.expirationTime = expirationTime\n                state.affected = true\n            else\n                state.duration = cooldownInfo.duration\n                state.expirationTime = cooldownInfo.expirationTime\n                state.affected = false\n            end\n        end\n        \n        changed = true\n    end\n    \n    return changed\nend",
