["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        aura_env:ShowOptionsStates(allstates)\n    elseif event == \"LIQUID_IMMUNITIES_SHOW\" then\n        local GUID, anchorFrame = ...\n        \n        aura_env:ShowStates(allstates, GUID, anchorFrame)\n        \n        return true\n    elseif event == \"LIQUID_IMMUNITIES_HIDE\" then\n        local GUID, anchorFrame = ...\n        \n        aura_env:HideStates(allstates, GUID, anchorFrame)\n        \n        return true\n    elseif event == \"LIQUID_CooldownListUpdate\" then\n        local id, unit, unitCooldowns = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local GUID = UnitGUID(unit)\n        local updated = false\n        \n        if unitCooldowns then\n            for spellID, cooldownInfo in pairs(unitCooldowns) do\n                updated = updated or aura_env:UpdateCooldownInfo(allstates, GUID, spellID, cooldownInfo)\n            end\n        end\n        \n        return updated\n    elseif event == \"LIQUID_CooldownListWipe\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        return aura_env:UpdateAllCooldownInfo(allstates)\n    elseif event == \"LIQUID_CooldownUpdate\" or event == \"LIQUID_CooldownAdded\" then\n        local id, unit, spellID, cooldownInfo = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local GUID = UnitGUID(unit)\n        \n        return aura_env:UpdateCooldownInfo(allstates, GUID, spellID, cooldownInfo)\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, _, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        return aura_env:UpdateStates(allstates, destGUID, spellID)\n    elseif event == \"ENCOUNTER_START\" or event == \"LIQUID_ENCOUNTER_START\" then\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env:UpdateAllCooldownInfo(allstates)\n    end\nend",
