["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local assignmentType = math.random(2) == 1 and \"SPAWN\" or \"BREAK\"\n        local icon = assignmentType == \"SPAWN\" and 4914671 or 967514\n        \n        for position = 1, assignmentType == \"SPAWN\" and 4 or 6 do\n            local mark = string.format(\"{rt%d}\", position)\n            \n            allstates[position] = {\n                show = true,\n                changed = true,\n                GUID = UnitGUID(\"player\"),\n                mark = mark,\n                icon = icon,\n                assignmentType = assignmentType,\n                index = position\n            }\n        end\n    elseif event == \"LIQUID2_FRACTILLUS_ASSIGNMENT\" then\n        local assignmentType, GUID, position = ...\n        \n        if not position then return end\n        \n        local unit = aura_env.GUIDToUnit[GUID]\n        local inLiquid = LiquidAPI and LiquidAPI.IsPotentialMember and LiquidAPI:IsPotentialMember(unit)\n        local mark = string.format(\"{rt%d}\", position)\n        local icon = assignmentType == \"SPAWN\" and 4914671 or 967514\n        \n        if not (inLiquid and aura_env.config.nonLiquidOnly) then\n            allstates[GUID] = {\n                show = true,\n                changed = true,\n                GUID = GUID,\n                mark = mark,\n                icon = 4914671,\n                assignmentType = assignmentType,\n                index = position\n            }\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1233416 or spellID == 1220394 or spellID == 1231871 then -- Crystalline Eruption/Shattering Backhand/Shockwave Slam\n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n    elseif event == \"STATUS\" then\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
