["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        if math.random(3) == 1 then\n            local position = math.random(6)\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = aura_env.config.safeDuration,\n                expirationTime = GetTime() + aura_env.config.safeDuration,\n                assignment = string.format(\"|cFF22FF3CSafe|r {rt%d}\", math.random(position)),\n                position = position,\n                autoHide = true\n            }\n        else\n            local position = math.random(6)\n            local assignment = string.format(\"%s on {rt%d}\", math.random(2) == 1 and \"|cFFFF1D2DBreak|r\" or \"|cFF4EE8FFSpawn|r\", position)\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                assignment = assignment,\n                position = position\n            }\n        end\n    elseif event == \"LIQUID2_FRACTILLUS_ASSIGNMENT\" then\n        local assignmentType, GUID, position = ...\n        \n        if not position then return end\n        if GUID ~= WeakAuras.myGUID then return end\n        \n        local assignment = string.format(\"%s on {rt%d}\", assignmentType == \"BREAK\" and \"|cFFFF1D2DBreak|r\" or \"|cFF4EE8FFSpawn|r\", position)\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            assignment = assignment,\n            position = position\n        }\n        \n        return true\n    elseif event == \"LIQUID2_FRACTILLUS_SAFESPOT\" then\n        local safespot = ...\n        \n        if not safespot then return end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = aura_env.config.safeDuration,\n            expirationTime = GetTime() + aura_env.config.safeDuration,\n            assignment = string.format(\"|cFF22FF3CSafe|r {rt%d}\", safespot),\n            position = safespot,\n            autoHide = true\n        }\n        \n        if aura_env.config.tts and safespot ~= aura_env.safespot then\n            local tts = aura_env.safespotTTS[safespot]\n            \n            LiquidWeakAuras:PlaySound(tts)\n        end\n        \n        aura_env.safespot = safespot\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1233416 or spellID == 1220394 or spellID == 1231871 then -- Crystalline Eruption/Shattering Backhand/Shockwave Slam\n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n    elseif event == \"STATUS\" then\n        aura_env.safespot = nil\n    end\nend",
