["source"] = "import",
["customGrow"] = "function(newPositions, activeRegions)\n    local degrees = 100\n    local slices = 6\n    local widthMultiplier = 0.82\n    local backgroundMinX = 0\n    local backgroundHeight\n    \n    local xMin = math.huge\n    local xMax = -math.huge\n    local yMin = math.huge\n    local yMax = -math.huge\n    \n    -- Lanes\n    for i, regionData in ipairs(activeRegions) do\n        local laneNumber = regionData.id:match(\"lane (%d)\")\n        \n        if laneNumber then\n            laneNumber = tonumber(laneNumber)\n            \n            local region = regionData.region\n            \n            local height = region.height\n            local circumference = 2 * math.pi * height / (360 / degrees)\n            local width = (circumference / slices) * widthMultiplier\n            local rotation = (laneNumber - 0.5 - (slices / 2)) * (2 * math.pi * ((degrees / 360) / slices))\n            local sideLength = math.sqrt(height * height + 0.25 * (width * width)) -- Length of the long side of the triangle\n            \n            region:RotateTextures(rotation, 0.5, 0.92)\n            region:SetRegionWidth(width)\n            region:SetRegionHeight(height)\n            \n            backgroundHeight = height\n            backgroundMinX = math.min(math.cos(math.rad(270) + rotation) * sideLength, backgroundMinX)\n            \n            xMin = math.min(xMin, -0.5 * width)\n            xMax = math.max(xMax, 0.5 * width)\n            \n            yMin = math.min(yMin, -0.5 * height)\n            yMax = math.max(yMax, 0.5 * height)\n            \n            newPositions[i] = {0, 0}\n        end\n    end\n    \n    -- Background/text\n    for i, regionData in ipairs(activeRegions) do\n        local isBackground = regionData.id:match(\"background\")\n        local isText = regionData.id:match(\"text\")\n        \n        if isBackground then\n            local region = regionData.region\n            \n            region:SetRegionWidth(2 * backgroundMinX)\n            region:SetRegionHeight(backgroundHeight or 0)\n            \n            newPositions[i] = {0, 0}\n        elseif isText then\n            newPositions[i] = {0, 70}\n        end\n    end\n    \n    -- Center the graphic\n    local xOffset = 0.5 * (xMax - math.abs(xMin))\n    local yOffset = 0.5 * (yMax - math.abs(yMin))\n    \n    for _, position in ipairs(newPositions) do\n        position[1] = position[1] - xOffset\n        position[2] = position[2] - yOffset\n    end\nend\n\n\n\n\n",
