{
["trigger"] = {
["type"] = "custom",
["subeventSuffix"] = "_CAST_START",
["customVariables"] = "{\n    spawned = {\n        display = \"Spawned\",\n        type = \"bool\"\n    },\n    wallType = {\n        display = \"Wall type\",\n        type = \"select\",\n        values = {\n            NORMAL = \"Normal\",\n            HEROIC = \"Heroic\",\n            MYTHIC = \"Mythic\"\n        }\n    },\n    row = {\n        type = \"number\",\n        display = \"Row\"\n    }\n}",
["event"] = "Health",
["unit"] = "player",
["custom_type"] = "stateupdate",
["subeventPrefix"] = "SPELL",
["events"] = "LIQUID_CUSTOM_EVENT, UNIT_SPELLCAST_START:boss, UNIT_SPELLCAST_SUCCEEDED:group, CLEU:SPELL_AURA_APPLIED:SPELL_CAST_SUCCESS, ENCOUNTER_START",
["names"] = {
},
["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local wallCounts = {\n            math.random(5),\n            math.random(5),\n            math.random(5),\n            math.random(5),\n            math.random(5),\n            math.random(5)\n        }\n        \n        local wallTypes = {\n            \"NORMAL\",\n            \"HEROIC\",\n            \"MYTHIC\",\n            \"MYTHIC\",\n            \"HEROIC\",\n            \"NORMAL\"\n        }\n        \n        for column = 1, 6 do\n            for row = 1, 6 do\n                allstates[string.format(\"%d-%d\", row, column)] = {\n                    show = true,\n                    changed = true,\n                    row = row,\n                    column = column,\n                    wallType = wallTypes[row],\n                    width = aura_env.width,\n                    height = aura_env.height,\n                    icon = \"Interface/AddOns/WeakAuras/Media/Textures/Square_FullWhite\",\n                    row = row,\n                    mark = string.format(\"{rt%d}\", column),\n                    spawned = row <= wallCounts[column]\n                }\n            end\n        end\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        for column = 1, 6 do\n            for row = 1, 6 do\n                allstates[string.format(\"%d-%d\", row, column)] = {\n                    show = true,\n                    changed = true,\n                    row = row,\n                    column = column,\n                    wallType = aura_env.wallTypes[row],\n                    width = aura_env.width,\n                    height = aura_env.height,\n                    icon = \"Interface/AddOns/WeakAuras/Media/Textures/Square_FullWhite\",\n                    row = row,\n                    mark = string.format(\"{rt%d}\", column),\n                    spawned = row <= aura_env.wallCounts[column]\n                }\n            end\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1231871 then -- Shockwave Slam\n            for unit in WA_IterateGroupMembers() do\n                local isTanking = UnitDetailedThreatSituation(unit, \"boss1\")\n                \n                if isTanking then\n                    aura_env.tankSpawnGUID = UnitGUID(unit)\n                    \n                    return\n                end\n            end\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            if spellID == 1233411 or spellID == 1246806 then -- Crystalline Shockwave/Entropic Conjunction\n                aura_env.spawnGUIDs[destGUID] = true\n            elseif spellID == 1227373 then -- Shattershell\n                aura_env.breakGUIDs[destGUID] = true\n            end\n            \n        elseif subEvent == \"SPELL_CAST_SUCCESS\" then\n            local changed = false\n            \n            if spellID == 1233416 then -- Crystalline Eruption (spawns)\n                for GUID in pairs(aura_env.spawnGUIDs) do\n                    local column = aura_env.columns[GUID]\n                    \n                    aura_env.wallCounts[column] = aura_env.wallCounts[column] + 1\n                end\n                \n                aura_env.spawnGUIDs = {}\n                \n                changed = true\n            elseif spellID == 1231871 then -- Shockwave Slam (tank spawn)\n                local GUID = aura_env.tankSpawnGUID\n                local column = aura_env.columns[GUID]\n                \n                aura_env.wallCounts[column] = aura_env.wallCounts[column] + aura_env.tankSpawnCount\n                \n                changed = true\n            elseif spellID == 1220394 then -- Shattering Backhand (breaks)\n                for GUID in pairs(aura_env.breakGUIDs) do\n                    local column = aura_env.columns[GUID]\n                    \n                    aura_env.wallCounts[column] = aura_env.wallCounts[column] - 1\n                end\n                \n                aura_env.breakGUIDs = {}\n                \n                changed = true\n            end\n            \n            if changed then\n                WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local unit, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        -- Column changes\n        local column = aura_env.spellIDToColumn[spellID]\n        \n        if column then\n            local GUID = UnitGUID(unit)\n            \n            aura_env.columns[GUID] = column\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.spawnGUIDs = {} -- GUIDs of players affected by Nether Crystallization\n        aura_env.breakGUIDs = {} -- GUIDs of players affected by Shattershell\n        aura_env.tankSpawnGUID = nil -- GUID of tank targeted by Shockwave Slam\n        \n        aura_env.columns = {} -- Table of GUIDs to columns\n        aura_env.wallCounts = {0, 0, 0, 0, 0, 0} -- Number of walls per column\n        \n        if difficultyID == 14 then -- Normal\n            aura_env.tankSpawnCount = 1\n            \n            aura_env.wallTypes = {\n                \"NORMAL\",\n                \"NORMAL\",\n                \"NORMAL\",\n                \"NORMAL\",\n                \"NORMAL\",\n                \"NORMAL\"\n            }\n        elseif difficultyID == 15 then -- Heroic\n            aura_env.tankSpawnCount = 1\n            \n            aura_env.wallTypes = {\n                \"NORMAL\",\n                \"HEROIC\",\n                \"NORMAL\",\n                \"NORMAL\",\n                \"HEROIC\",\n                \"NORMAL\"\n            }\n        elseif difficultyID == 16 then -- Mythic\n            aura_env.tankSpawnCount = 3\n            \n            aura_env.wallTypes = {\n                \"NORMAL\",\n                \"HEROIC\",\n                \"MYTHIC\",\n                \"MYTHIC\",\n                \"HEROIC\",\n                \"NORMAL\"\n            }\n        end\n        \n        -- Create initial states\n        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n    end\nend",
