["events"] = "CLEU:SPELL_AURA_APPLIED, LIQUID_ASSIGNMENT_EVENT, UNIT_SPELLCAST_SUCCEEDED:boss, UNIT_SPELLCAST_START:boss, ENCOUNTER_START",
["custom"] = "function(event, ...)\n    if event ~= \"ENCOUNTER_START\" and aura_env.disabled then return end\n    \n    if event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id, assignmentType = ...\n        local affected, positions\n        \n        if id ~= aura_env.id then return end\n        \n        if assignmentType == \"SPAWN\" then\n            affected = aura_env.spawnGUIDs\n            positions = table.remove(aura_env.spawns, 1)\n        elseif assignmentType == \"BREAK\" then\n            affected = aura_env.breakGUIDs\n            positions = table.remove(aura_env.breaks, 1)\n        end\n        \n        if not positions then return end\n        \n        table.sort(\n            affected,\n            function(guidA, guidB)\n                local orderA = aura_env.breakOrder[guidA] or 0\n                local orderB = aura_env.breakOrder[guidB] or 0\n                \n                if orderA ~= orderB then\n                    return orderA < orderB\n                end\n                \n                return guidA < guidB\n            end\n        )\n        \n        -- Assign positions\n        local breakTypes = {0, 0} -- Heroic, Mythic\n        \n        for i, GUID in ipairs(affected) do\n            local position = positions[i]\n            \n            if position then\n                WeakAuras.ScanEvents(\"LIQUID2_FRACTILLUS_ASSIGNMENT\", assignmentType, GUID, position)\n            end\n            \n            -- Update wall counts\n            local currentWallCount = aura_env.wallCounts[position]\n            \n            if assignmentType == \"SPAWN\" then\n                aura_env.wallCounts[position] = math.min(currentWallCount + 1, 6)\n            elseif assignmentType == \"BREAK\" then\n                local wallType = aura_env.wallTypes[currentWallCount]\n                \n                if wallType == \"HEROIC\" then\n                    breakTypes[1] = breakTypes[1] + 1\n                elseif wallType == \"MYTHIC\" then\n                    breakTypes[2] = breakTypes[2] + 1\n                end\n                \n                aura_env.wallCounts[position] = math.max(currentWallCount - 1, 0)\n            end\n            \n            if aura_env.config.TTS and GUID == WeakAuras.myGUID then\n                local marker = string.format(\"{rt%d}\", position)\n                \n                LiquidWeakAuras:PlaySound(marker)\n            end\n        end\n        \n        if assignmentType == \"SPAWN\" then\n            aura_env.spawnGUIDs = {}\n        elseif assignmentType == \"BREAK\" then\n            WeakAuras.ScanEvents(\"LIQUID_FRACTILLUS_BREAK_COUNTS\", breakTypes[1], breakTypes[2])\n            \n            aura_env.breakGUIDs = {}\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1233416 or spellID == 1231871 then -- Crystalline Eruption/Shockwave Slam\n            aura_env.safespot = table.remove(aura_env.safespots, 1)\n            \n            WeakAuras.ScanEvents(\"LIQUID2_FRACTILLUS_SAFESPOT\", aura_env.safespot)\n        elseif spellID == 1220394 then -- Shattering Backhand\n            WeakAuras.ScanEvents(\"LIQUID2_FRACTILLUS_SAFESPOT\", aura_env.safespot)\n        end\n        \n        if spellID == 1231871 then -- Shockwave Slam\n            WeakAuras.ScanEvents(\"LIQUID_SHOCKWAVE_SLAM\", aura_env.tankSpawns[1], aura_env.config.tts)\n        end\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1231871 then -- Shockwave Slam\n            for unit in WA_IterateGroupMembers() do\n                local isTanking = UnitDetailedThreatSituation(unit, \"boss1\")\n                \n                if isTanking then\n                    local GUID = UnitGUID(unit)\n                    local position = table.remove(aura_env.tankSpawns, 1)\n                    \n                    aura_env.wallCounts[position] = math.min(aura_env.wallCounts[position] + 3, 6)\n                    \n                    WeakAuras.ScanEvents(\"LIQUID2_FRACTILLUS_ASSIGNMENT\", \"SPAWN\", GUID, position)\n                    \n                    return\n                end\n            end\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            if spellID == 1233411 then -- Crystalline Shockwave\n                table.insert(aura_env.spawnGUIDs, destGUID)\n                \n                if #aura_env.spawnGUIDs == 1 then\n                    local id = aura_env.id\n                    \n                    C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id, \"SPAWN\") end)\n                end\n            elseif spellID == 1227373 then -- Shattershell\n                table.insert(aura_env.breakGUIDs, destGUID)\n                \n                if #aura_env.breakGUIDs == 1 then\n                    local id = aura_env.id\n                    \n                    C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id, \"BREAK\") end)\n                end\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        \n        aura_env.GUIDToUnit = {}\n        aura_env.spawnGUIDs = {}\n        aura_env.breakGUIDs = {}\n        aura_env.wallCounts = {0, 0, 0, 0, 0, 0}\n        aura_env.wallTypes = {\"NORMAL\", \"NORMAL\", \"NORMAL\", \"NORMAL\", \"NORMAL\", \"NORMAL\"}\n        \n        if difficultyID == 15 then -- Heroic\n            aura_env.wallTypes[2] = \"HEROIC\"\n            aura_env.wallTypes[5] = \"HEROIC\"\n        end\n        \n        if difficultyID == 16 then -- Mythic\n            aura_env.wallTypes[2] = \"HEROIC\"\n            aura_env.wallTypes[3] = \"MYTHIC\"\n            aura_env.wallTypes[4] = \"MYTHIC\"\n            aura_env.wallTypes[5] = \"HEROIC\"\n        end\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env:ReadNoteAssignments(difficultyID)\n        \n        -- Broadcast the initial safespot\n        aura_env.safespot = table.remove(aura_env.safespots, 1)\n        \n        WeakAuras.ScanEvents(\"LIQUID2_FRACTILLUS_SAFESPOT\", aura_env.safespot)\n        WeakAuras.ScanEvents(\"LIQUID_SHOCKWAVE_SLAM\", aura_env.tankSpawns[1], aura_env.config.tts)\n    end\nend",
