["events"] = "UNIT_SPELLCAST_START:boss, LIQUID_CUSTOM_EVENT",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local castType = math.random(2) == 1 and \"CAST\" or \"CHANNEL\"\n        local duration = castType == \"CAST\" and 2 or 4\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration,\n            expirationTime = GetTime() + duration,\n            castType = castType,\n            count = math.random(3),\n            autoHide = true\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id, castType, count = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local duration = castType == \"CAST\" and 2 or 4\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration,\n            expirationTime = GetTime() + duration,\n            castType = castType,\n            count = count,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1228163 then return end -- Dimension Breath\n        \n        local id = aura_env.id\n        \n        C_Timer.After(0, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, \"CAST\", 1) end)\n        C_Timer.After(2, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, \"CHANNEL\", 1) end)\n        \n        C_Timer.After(6, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, \"CAST\", 2) end)\n        C_Timer.After(8, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, \"CHANNEL\", 2) end)\n        \n        C_Timer.After(12, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, \"CAST\", 3) end)\n        C_Timer.After(14, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, \"CHANNEL\", 3) end)\n    end\nend",
