["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 3,\n            expirationTime = GetTime() + 3,\n            count = math.random(9),\n            autoHide = true\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id, count, duration = ...\n        \n        if id ~= aura_env.id then return end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration,\n            expirationTime = GetTime() + duration,\n            count = count,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1228317 then return end -- King's Hunger\n        \n        local id = aura_env.id\n        \n        C_Timer.After(0, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\",    id, 1, 2.5) end)\n        C_Timer.After(2.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\",  id, 2, 3) end)\n        C_Timer.After(5.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\",  id, 3, 3) end)\n        C_Timer.After(8.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\",  id, 4, 3) end)\n        C_Timer.After(11.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 5, 3) end)\n        C_Timer.After(14.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 6, 3) end)\n        C_Timer.After(17.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 7, 3) end)\n        C_Timer.After(20.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 8, 3) end)\n        C_Timer.After(23.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 9, 3) end)\n       