["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 5.4,\n            expirationTime = GetTime() + 5.4,\n            set = 3,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id, set = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if set == 1 then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 3,\n                expirationTime = GetTime() + 3,\n                set = 1,\n                autoHide = true\n            }\n        elseif set == 2 then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 4.2,\n                expirationTime = GetTime() + 4.2,\n                set = 2,\n                autoHide = true\n            }\n        elseif set == 3 then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 5.4,\n                expirationTime = GetTime() + 5.4,\n                set = 3,\n                autoHide = true\n            }\n        end\n        \n        if aura_env.config.countdown then\n            LiquidWeakAuras:PlaySound(set)\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1224787 and spellID ~= 1224812 then return end -- Conquer/Vanquish\n        \n        aura_env.count = aura_env.count + 1\n        \n        local id = aura_env.id\n        \n        if aura_env.count == 1 then\n            C_Timer.After(32, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 1) end)\n        elseif aura_env.count == 5 then\n            C_Timer.After(32, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 2) end)\n        elseif aura_env.count == 9 then\n            C_Timer.After(16.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id, 3) end)\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 0\n    end\nend",
