["subeventPrefix"] = "SPELL",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            soak = math.random(2) == 1,\n            autoHide = true\n        }\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1224787 and spellID ~= 1224812 then return end -- Conquer/Vanquish\n        \n        aura_env.count = aura_env.count + 1\n        \n        if aura_env.count == 5 then\n            aura_env.count = 1\n        end\n        \n        if spellID == 1224787 then -- Conquer\n            local soak = aura_env.count == 1 or aura_env.count == 3\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 4,\n                expirationTime = GetTime() + 4,\n                soak = soak,\n                autoHide = true\n            }\n            \n            if aura_env.config.tts then\n                LiquidWeakAuras:PlaySound(soak and \"soak\" or \"dont soak\")\n            end\n            \n            return true\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 0\n    end\nend",
