["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6,\n            expirationTime = GetTime() + 6,\n            autoHide = true\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6,\n            expirationTime = GetTime() + 6,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local unit, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1228065 then -- Rally the Shadowguard\n            local id = aura_env.id\n            \n            aura_env.timer = C_Timer.NewTimer(21, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id) end)\n        elseif spellID == 1232399 then -- Dread Mortar\n            local inRange = WeakAuras.CheckRange(unit, 40, \"<=\")\n            \n            if not inRange then return end\n            \n            if aura_env.timer then\n                aura_env.timer:Cancel()\n            end\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 6,\n                expirationTime = GetTime() + 3,\n                autoHide = true\n            }\n            \n            -- Start timer for the next cast\n            local id = aura_env.id\n            \n            aura_env.timer = C_Timer.NewTimer(21.5, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id) end)\n            \n            return true\n        end\n    end\nend",
