["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            assignment = math.random(2) == 1 and \"Left\" or \"Right\"\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        if not aura_env.enabled then return end\n        \n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local assignedGUIDs = CopyTable(aura_env.assignments[aura_env.count])\n        \n        if not assignedGUIDs then return end\n        \n        local backups = CopyTable(aura_env.backups)\n        \n        -- Hide existing state\n        local state = allstates[\"\"]\n        \n        if state then\n            state.show = false\n            state.changed = true\n        end\n        \n        -- Remove ineligble backups\n        for index, GUID in ipairs_reverse(backups) do\n            local unit = aura_env.GUIDToUnit[GUID]\n            local isDead = UnitIsDeadOrGhost(unit)\n            \n            if isDead then\n                table.remove(backups, index)\n            end\n        end\n        \n        -- Assign backups if necessary\n        for index, GUID in ipairs(assignedGUIDs) do\n            local unit = aura_env.GUIDToUnit[GUID]\n            local isDead = UnitIsDeadOrGhost(unit)\n            \n            if isDead then\n                assignedGUIDs[index] = table.remove(backups, 1)\n            end\n        end\n        \n        -- Show state if assigned\n        for index, GUID in ipairs(assignedGUIDs) do\n            local assignment = index == 1 and \"Left\" or \"Right\"\n            \n            if GUID == WeakAuras.myGUID then\n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    assignment = assignment\n                }\n            end\n            \n            WeakAuras.ScanEvents(\"LIQUID_STARKILLER_SWING_ASSIGNMENT\", GUID, assignment)\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        if not aura_env.enabled then return end\n        \n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1226024 then return end -- Starkiller Swing\n        \n        aura_env.count = aura_env.count + 1\n        \n        WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID = ...\n        local isGroupMember = destGUID and aura_env.GUIDToUnit[destGUID]\n        \n        if subEvent == \"UNIT_DIED\" and isGroupMember then\n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"LIQUID_UNIT_ALIVE\" then\n        WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 1 -- Starkiller Swing count\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env:ReadNote()\n    end\nend",
