["custom"] = "aura_env.enabled = C_AddOns.IsAddOnLoaded(\"AuraUpdater\") or C_AddOns.IsAddOnLoaded(\"LiquidMedia\")\n\nlocal auraData = WeakAuras.GetData(\"Galactic Smash background (requires AuraUpdater)\")\n\nlocal MAP_WIDTH = auraData and auraData.width or 200\nlocal MAP_HEIGHT = auraData and auraData.height or 200\nlocal CIRCLE_SIZE = 0.12\n\n-- Relative to center\nlocal INNER_RING_DISTANCE = 0.185\nlocal MIDDLE_RING_DISTANCE = 0.298\nlocal OUTER_RING_DISTANCE = 0.42\n\nlocal MAP_CENTER_OFFSET_X = 0 * MAP_WIDTH\nlocal MAP_CENTER_OFFSET_Y = 0.01 * MAP_HEIGHT\n\n-- Heroic positions\nlocal heroicDegrees = {\n    {\n        [1] = 90, -- Ranged 1\n        [2] = 116, -- Ranged 2\n        [3] = 142, -- Ranged 3\n        [4] = 186,  -- Melee 1\n        [5] = 228, -- Tank\n        [6] = 270, -- Melee 2\n    },\n    {\n        [1] = 90, -- Ranged 1\n        [2] = 116, -- Ranged 2\n        [3] = 142, -- Ranged 3\n        [4] = 186,  -- Melee 1\n        [5] = 228, -- Tank\n        [6] = 270, -- Melee 2\n    },\n    {\n        [1] = 90, -- Ranged 1\n        [2] = 116, -- Ranged 2\n        [3] = 142, -- Ranged 3\n        [4] = 186,  -- Melee 1\n        [5] = 228, -- Tank\n        [6] = 270, -- Melee 2\n    },\n}\n\nlocal heroicDistances = {\n    {\n        [1] = MIDDLE_RING_DISTANCE, -- Ranged 1\n        [2] = MIDDLE_RING_DISTANCE, -- Ranged 2\n        [3] = MIDDLE_RING_DISTANCE, -- Ranged 3\n        [4] = INNER_RING_DISTANCE,  -- Melee 1\n        [5] = INNER_RING_DISTANCE,  -- Tank\n        [6] = INNER_RING_DISTANCE,  -- Melee 2\n    },\n    {\n        [1] = MIDDLE_RING_DISTANCE, -- Ranged 1\n        [2] = MIDDLE_RING_DISTANCE, -- Ranged 2\n        [3] = MIDDLE_RING_DISTANCE, -- Ranged 3\n        [4] = INNER_RING_DISTANCE,  -- Melee 1\n        [5] = INNER_RING_DISTANCE,  -- Tank\n        [6] = INNER_RING_DISTANCE,  -- Melee 2\n    },\n    {\n        [1] = MIDDLE_RING_DISTANCE, -- Ranged 1\n        [2] = MIDDLE_RING_DISTANCE, -- Ranged 2\n        [3] = MIDDLE_RING_DISTANCE, -- Ranged 3\n        [4] = INNER_RING_DISTANCE,  -- Melee 1\n        [5] = INNER_RING_DISTANCE,  -- Tank\n        [6] = INNER_RING_DISTANCE,  -- Melee 2\n    },\n}\n\nlocal heroicPositions = {{}, {}, {}}\n\nfor i = 1, 3 do\n    for j = 1, 6 do\n        local degree = heroicDegrees[i][j]\n        local distance = heroicDistances[i][j]\n        \n        heroicPositions[i][j] = {\n            math.cos(math.rad(degree)) * distance * MAP_WIDTH + MAP_CENTER_OFFSET_X,\n            math.sin(math.rad(degree)) * distance * MAP_HEIGHT + MAP_CENTER_OFFSET_Y,\n        }\n    end\nend\n\n-- Mythic positions\nlocal mythicDegrees = {\n    {\n        [1] = 251,  -- Ranged 1\n        [2] = 270, -- Ranged 2\n        [3] = 289, -- Ranged 3\n        [4] = 15, -- Melee 1\n        [5] = 333,  -- Tank\n        [6] = 291, -- Melee 2\n    },\n    {\n        [1] = 251,  -- Ranged 1\n        [2] = 270, -- Ranged 2\n        [3] = 289, -- Ranged 3\n        [4] = 334, -- Melee 1\n        [5] = 0,  -- Tank\n        [6] = 26, -- Melee 2\n    },\n    {\n        [1] = 341,  -- Ranged 1\n        [2] = 0, -- Ranged 2\n        [3] = 19, -- Ranged 3\n        [4] = 103, -- Melee 1\n        [5] = 129,  -- Tank\n        [6] = 155, -- Melee 2\n    },\n}\n\nlocal mythicDistances = {\n    {\n        [1] = OUTER_RING_DISTANCE, -- Ranged 1\n        [2] = OUTER_RING_DISTANCE, -- Ranged 2\n        [3] = OUTER_RING_DISTANCE, -- Ranged 3\n        [4] = INNER_RING_DISTANCE, -- Melee 1\n        [5] = INNER_RING_DISTANCE, -- Tank\n        [6] = INNER_RING_DISTANCE, -- Melee 2\n    },\n    {\n        [1] = OUTER_RING_DISTANCE, -- Ranged 1\n        [2] = OUTER_RING_DISTANCE, -- Ranged 2\n        [3] = OUTER_RING_DISTANCE, -- Ranged 3\n        [4] = MIDDLE_RING_DISTANCE, -- Melee 1\n        [5] = MIDDLE_RING_DISTANCE, -- Tank\n        [6] = MIDDLE_RING_DISTANCE, -- Melee 2\n    },\n    {\n        [1] = OUTER_RING_DISTANCE, -- Ranged 1\n        [2] = OUTER_RING_DISTANCE, -- Ranged 2\n        [3] = OUTER_RING_DISTANCE, -- Ranged 3\n        [4] = MIDDLE_RING_DISTANCE, -- Melee 1\n        [5] = MIDDLE_RING_DISTANCE, -- Tank\n        [6] = MIDDLE_RING_DISTANCE, -- Melee 2\n    },\n}\n\nlocal mythicPositions = {{}, {}, {}}\n\nfor i = 1, 3 do\n    for j = 1, 6 do\n        local degree = mythicDegrees[i][j]\n        local distance = mythicDistances[i][j]\n        \n        mythicPositions[i][j] = {\n            math.cos(math.rad(degree)) * distance * MAP_WIDTH + MAP_CENTER_OFFSET_X,\n            math.sin(math.rad(degree)) * distance * MAP_HEIGHT + MAP_CENTER_OFFSET_Y,\n        }\n    end\nend\n\nlocal indexToType = {\n    \"RANGED\",\n    \"RANGED\",\n    \"RANGED\",\n    \n    \"MELEE\",\n    \"TANK\",\n    \"MELEE\"\n}\n\nfunction aura_env:CreateStates(allstates, set, assignmentType, duration)\n    local assignmentTypesToShow = {\n        [assignmentType] = true -- Always show our own assignmentType\n    }\n    \n    if aura_env.config.showAll then\n        assignmentTypesToShow.MELEE = true\n        assignmentTypesToShow.RANGED = true\n        assignmentTypesToShow.TANK = true\n    elseif assignmentType ~= \"RANGED\" then\n        assignmentTypesToShow.MELEE = true\n        assignmentTypesToShow.TANK = true\n    end\n    \n    local positions = aura_env.isMythic and mythicPositions or heroicPositions\n    \n    for index, position in ipairs(positions[set]) do\n        local positionType = indexToType[index]\n        local shouldShow = assignmentTypesToShow[positionType]\n        local assigned = positionType == assignmentType\n        \n        if shouldShow then\n            allstates[index] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = duration,\n                expirationTime = GetTime() + duration,\n                width = CIRCLE_SIZE * MAP_WIDTH,\n                height = CIRCLE_SIZE * MAP_HEIGHT,\n                assigned = assigned,\n                position = position,\n                autoHide = true\n            }\n        end\n    end\nend",
