["custom"] = "local region = aura_env.region\nlocal frame\n\n-- Find texture subregion (prefer this over just picking index 3, in case the user changed something)\nfor _, subRegion in ipairs(region.subRegions) do\n    if subRegion.type == \"subtexture\" then\n        frame = subRegion\n        \n        break\n    end\nend\n\nif frame then\n    local anchorID = region.anchorID\n    \n    if anchorID then\n        C_UnitAuras.RemovePrivateAuraAnchor(anchorID)\n    end\n    \n    local privateAnchorArgs = {\n        unitToken = \"player\",\n        auraIndex = 1,\n        parent = frame,\n        showCountdownFrame = false,\n        showCountdownNumbers = false,\n        iconInfo = {\n            iconAnchor = {\n                point = \"CENTER\",\n                relativeTo = frame,\n                relativePoint = \"CENTER\",\n                offsetX = 0,\n                offsetY = 0\n            },\n            iconWidth = frame:GetWidth(),\n            iconHeight = frame:GetHeight()\n        }\n    }\n    \n    region.anchorID = C_UnitAuras.AddPrivateAuraAnchor(privateAnchorArgs)\nend\n\nfunction aura_env:ReadNote()\n    local lineCount = 0\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"Star\") do\n        local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n        \n        if next(GUIDs) then\n            lineCount = lineCount + 1\n        end\n        \n        -- This aura is disabled if more than a single line of names is provided\n        -- In this case we assume the Starkiller Swings are hard assigned\n        if lineCount > 1 then\n            return false\n        end\n    end\n    \n    return true\nend",
