["custom"] = "function(event, ...)\n    if event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        if aura_env.disabled then return end\n        \n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1224822 then -- Tyranny\n            -- Determine assignment type\n            local isTank = UnitGroupRolesAssigned(\"player\") == \"TANK\"\n            local isRanged = LiquidWeakAuras:Spec(\"player\") == \"r\"\n            local assignmentType = isTank and \"TANK\" or isRanged and \"RANGED\" or \"MELEE\"\n            \n            for set, spawnTime in ipairs(aura_env.spawnTimes) do\n                local showTime = math.max(spawnTime - aura_env.config.duration, 0)\n                local duration = math.min(aura_env.config.duration, spawnTime)\n                \n                table.insert(\n                    aura_env.region.timers,\n                    C_Timer.NewTimer(\n                        showTime,\n                        function()\n                            WeakAuras.ScanEvents(\"LIQUID_GALACTIC_SMASH_ASSIGNMENT\", set, assignmentType, duration)\n                        end\n                    )\n                )\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        \n        -- Cancel all running timers\n        if aura_env.region.timers then\n            for _, timer in pairs(aura_env.region.timers) do\n                timer:Cancel()\n            end\n        end\n        \n        aura_env.region.timers = {}\n    end\nend",
