["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local orders = {\n            {\"VANQUISH\", \"VANQUISH\", \"CONQUER\", \"CONQUER\"},\n            {\"CONQUER\", \"CONQUER\", \"VANQUISH\", \"VANQUISH\"},\n            {\"VANQUISH\", \"CONQUER\", \"CONQUER\", \"VANQUISH\"},\n            {\"CONQUER\", \"VANQUISH\", \"VANQUISH\", \"CONQUER\"},\n        }\n        \n        aura_env.order = orders[math.random(4)]\n        \n        aura_env.assignments = {false, true}\n        aura_env.count = 2 + math.random(2)\n        \n        aura_env:UpdateStates(allstates)\n        aura_env:Start(allstates, aura_env.count)\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID ~= 1224787 and spellID ~= 1224812 then return end -- Conquer/Vanquish\n        \n        aura_env.count = aura_env.count + 1\n        aura_env.order[aura_env.count] = spellID == 1224787 and \"CONQUER\" or \"VANQUISH\"\n        \n        aura_env:UpdateOrder()\n        aura_env:UpdateStates(allstates)\n        aura_env:Start(allstates, aura_env.count)\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1224787 and spellID ~= 1224812 then return end -- Conquer/Vanquish\n        \n        -- Hide states after the 4th cast completes\n        if aura_env.count == 4 then\n            aura_env.order = {}\n            aura_env.count = 0\n            \n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.order = {}\n        aura_env.count = 0\n    end\nend",
