["init"] = {
["do_custom"] = true,
["custom"] = "local function SetConquer(allstates, index)\n    local assigned = aura_env.order[1] == \"CONQUER\" and index == 1 or aura_env.order[1] == \"VANQUISH\" and index == 3\n    \n    allstates[index] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = aura_env.count > index and 0 or 1,\n        total = 1,\n        castType = \"CONQUER\",\n        text = \"Conquer\",\n        index = index,\n        assigned = assigned,\n        autoHide = false\n    }\nend\n\nlocal function SetVanquish(allstates, index)\n    allstates[index] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = aura_env.count > index and 0 or 1,\n        total = 1,\n        castType = \"VANQUISH\",\n        index = index,\n        text = \"Vanquish\",\n        autoHide = false\n    }\nend\n\nlocal function SetUnknown(allstates, index)\n    allstates[index] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = 0,\n        total = 1,\n        castType = \"UNKNOWN\",\n        index = index,\n        autoHide = false\n    }\nend\n\nfunction aura_env:Start(allstates, index)\n    local state = allstates[index]\n    \n    if not state then return end\n    \n    local castType = state.castType\n    local duration = castType == \"CONQUER\" and 4 or 2.5\n    \n    state.changed = true\n    state.progressType = \"timed\"\n    state.duration = duration\n    state.expirationTime = GetTime() + duration\nend\n\nfunction aura_env:UpdateOrder()\n    local order = aura_env.order\n    \n    if order[1] == \"CONQUER\" then -- Conquer\n        order[3] = \"VANQUISH\"\n        \n        if order[2] == \"CONQUER\" then -- Conquer > Conquer\n            order[3] = \"VANQUISH\"\n            order[4] = \"VANQUISH\"\n        elseif order[2] == \"VANQUISH\" then -- Conquer > Vanquish\n            order[3] = \"VANQUISH\"\n            order[4] = \"CONQUER\"\n        end\n    elseif order[1] == \"VANQUISH\" then -- Vanquish\n        order[3] = \"CONQUER\"\n        \n        if order[2] == \"CONQUER\" then -- Vanquish > Conquer\n            order[3] = \"CONQUER\"\n            order[4] = \"VANQUISH\"\n        elseif order[2] == \"VANQUISH\" then -- Vanquish > Vanquish\n            order[3] = \"CONQUER\"\n            order[4] = \"CONQUER\"\n        end\n    end\nend\n\nfunction aura_env:UpdateStates(allstates)\n    -- Set state cast types\n    for index = 1, 4 do\n        local castType = aura_env.order[index]\n        \n        if castType == \"CONQUER\" then\n            SetConquer(allstates, index)\n        elseif castType == \"VANQUISH\" then\n            SetVanquish(allstates, index)\n        else\n            SetUnknown(allstates, index)\n        end\n    end\nend",
