["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local value = math.random(80)\n        local percent = (value / 80) * 100\n        \n        allstates[1] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            value = 80,\n            total = 80,\n            index = 1,\n            skipPosition = true,\n            percent = percent,\n            autoHide = GetTime() + 10\n        }\n        \n        allstates[2] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 10,\n            expirationTime = GetTime() + 10,\n            timer = true,\n            index = 2,\n            autoHide = true\n        }\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if (subEvent == \"SPELL_DAMAGE\" or subEvent == \"SPELL_MISSED\") and spellID == 1243702 then -- Antimatter\n            local unit = aura_env.GUIDToUnit[destGUID]\n            local inRange = UnitInRange(unit)\n            \n            if inRange then\n                local state = allstates[1]\n                \n                if state then\n                    state.changed = true\n                    state.value = state.value - 1\n                    \n                    -- If the soak reached 0, hide it\n                    if state.value <= 0 then\n                        state.show = false\n                        \n                        -- Hide timer state as well\n                        local timerState = allstates[2]\n                        \n                        if timerState then\n                            timerState.show = false\n                            timerState.changed = true\n                        end\n                    end\n                    \n                    return true\n                end\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1243690 then return end -- Shattered Space\n        \n        allstates[1] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            value = 80,\n            total = 80,\n            index = 1,\n            skipPosition = true,\n            percent = 100,\n            autoHide = GetTime() + 10\n        }\n        \n        allstates[2] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 10,\n            expirationTime = GetTime() + 10,\n            timer = true,\n            index = 2,\n            autoHide = true\n        }\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
