["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 8 do\n            local currentHealth = math.random() * 1000000\n            local totalHealth = 1000000\n            local mySide = i <= 4\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = currentHealth,\n                total = totalHealth,\n                index = mySide and string.format(\"1-%d\", i) or string.format(\"1-%d\", i),\n                raidMark = string.format(\"{rt%d}\", i),\n                mySide = mySide,\n                percentHealth = (currentHealth / totalHealth) * 100\n            }\n        end\n    elseif event == \"FRAME_UPDATE\" then\n        local states = WeakAuras.GetTriggerStateForTrigger(aura_env.id, 2)\n        \n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        \n        if not states then return true end\n        \n        local addCount = 0\n        local addDistances = {}\n        \n        for _, state in pairs(states) do\n            addCount = addCount + 1\n            \n            local unit = state.unit\n            local GUID = UnitGUID(unit)\n            local currentHealth = state.value\n            local totalHealth = state.total\n            local raidMark = state.raidMark\n            local distance = WeakAuras.GetRange(unit)\n            \n            table.insert(\n                addDistances,\n                {\n                    GUID = GUID,\n                    distance = distance or 100\n                }\n            )\n            \n            -- Don't create a new table for the state if we don't have to, so we preserve mySide\n            local existingState = allstates[GUID]\n            \n            if existingState then\n                existingState.show = true\n                existingState.changed = true\n                existingState.value = currentHealth\n                existingState.total = totalHealth\n                existingState.raidMark = raidMark\n                existingState.percentHealth = (currentHealth / totalHealth) * 100\n            else\n                allstates[GUID] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"static\",\n                    value = currentHealth,\n                    total = totalHealth,\n                    raidMark = raidMark,\n                    percentHealth = (currentHealth / totalHealth) * 100\n                }\n            end\n        end\n        \n        -- Desaturate the farthest away half of the adds\n        -- Only do this if all adds are still alive, otherwise just leave as they are\n        if addCount == aura_env.maxAddCount then\n            table.sort(\n                addDistances,\n                function(entryA, entryB)\n                    local distanceA = entryA.distance\n                    local distanceB = entryB.distance\n                    \n                    if distanceA ~= distanceB then\n                        return distanceA < distanceB\n                    end\n                    \n                    return entryA.GUID < entryB.GUID\n                end\n            )\n            \n            for index, entry in ipairs(addDistances) do\n                local GUID = entry.GUID\n                local mySide = index <= addCount / 2\n                local state = allstates[GUID]\n                \n                state.mySide = mySide\n                state.index = mySide and string.format(\"1-%s\", GUID) or string.format(\"2-%s\", GUID)\n            end\n        end\n        \n        return true\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1230087 then return end -- Massive Smash\n        \n        aura_env.maxAddCount = 4\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.maxAddCount = 6\n    end\nend",
