["custom"] = "if not aura_env.saved then aura_env.saved = {} end\n\nfunction aura_env:GetNumDeadPlayers()\n    local deadCount = 0\n    \n    for unit in WA_IterateGroupMembers() do\n        local isDead = UnitIsDeadOrGhost(unit)\n        \n        if isDead then\n            deadCount = deadCount + 1\n        end\n    end\n    \n    return deadCount\nend",
