["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 3,\n            expirationTime = GetTime() + 3,\n            autoHide = true\n        }\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        \n        if spellID == 1230979 then -- Dark Matter\n            aura_env.count = aura_env.count + 1\n            aura_env.castStart = GetTime()\n            \n            local duration = aura_env.durations[aura_env.count] or 3\n            \n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = duration,\n                expirationTime = GetTime() + duration,\n                autoHide = true\n            }\n            \n            return true\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_DAMAGE\" and destGUID == WeakAuras.myGUID and spellID == 1230999 then -- Dark Matter\n            local timeSinceCastStart = GetTime() - aura_env.castStart\n            \n            -- Sometimes players get hit before the cast finishes, but it seems to be \"fake\"\n            -- Skip these\n            if timeSinceCastStart < 2 then return end\n            \n            -- Hide state\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n            end\n            \n            -- Do not update durations if 4 or more players were dead\n            -- Players may not actually go to the correct spot if a wipe is called\n            local deadPlayerCount = aura_env:GetNumDeadPlayers()\n            \n            if deadPlayerCount >= 4 then return true end\n            \n            -- Update saved duration\n            aura_env.durations[aura_env.count] = timeSinceCastStart\n            \n            return true\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        local _, _, difficultyID = ...\n        \n        if not aura_env.saved[difficultyID] then aura_env.saved[difficultyID] = {} end\n        \n        aura_env.durations = aura_env.saved[difficultyID]\n        aura_env.count = 0\n        aura_env.visibleGUIDs = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.visibleGUIDs[GUID] = true\n        end\n    end\nend",
