["custom_type"] = "stateupdate",
["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            text = string.format(\"%d/3\", math.random(3))\n        }\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if aura_env.count == 0 then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 1,\n                text = \"|cFF23FF5ADone|r\",\n                expirationTime = GetTime() + 1,\n                autoHide = true\n            }\n            \n            LiquidWeakAuras:PlaySound(\"done\")\n            \n            return true\n        end\n        \n        if aura_env.count <= 0 then\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n            \n            return\n        end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            text = string.format(\"%d/3\", 3 - aura_env.count)\n        }\n        \n        if aura_env.count < 3 then\n            LiquidWeakAuras:PlaySound(3 - aura_env.count)\n        end\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_REMOVED\" and spellID == 1243609 then -- Airborne\n            local unit = aura_env.GUIDToUnit[destGUID]\n            local inRange = UnitInRange(unit)\n            \n            if inRange then\n                aura_env.count = aura_env.count - 1\n                \n                WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n            end\n        elseif subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1243609 then -- Airborne\n            local unit = aura_env.GUIDToUnit[destGUID]\n            local inRange = UnitInRange(unit)\n            \n            if inRange then\n                aura_env.count = aura_env.count + 1\n                \n                if aura_env.count == 1 then\n                    local id = aura_env.id\n                    \n                    C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", id) end)\n                end\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1230979 then return end -- Dark Matter\n        \n        aura_env.count = 0\n        \n        -- Hide state (safety measure)\n        local state = allstates[\"\"]\n        \n        if state then\n            state.show = false\n            state.changed = true\n            \n            return true\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.count = 0\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
