["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 4.8,\n            expirationTime = GetTime() + 4.8,\n            assignment = aura_env.assignments[math.random(3)],\n            autoHide = true\n        }\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        if aura_env.disabled then return end\n        \n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        table.sort(\n            aura_env.affected,\n            function(guidA, guidB)\n                if guidA and not guidB then return true end\n                if guidB and not guidA then return false end\n                \n                local unitA = aura_env.GUIDToUnit[guidA]\n                local unitB = aura_env.GUIDToUnit[guidB]\n                \n                local rangeA = LiquidWeakAuras:Spec(unitA) or \"\"\n                local rangeB = LiquidWeakAuras:Spec(unitB) or \"\"\n                \n                -- Sort melee before ranged\n                if rangeA ~= rangeB then\n                    return rangeA < rangeB\n                end\n                \n                if rangeA == \"m\" and rangeB == \"m\" then\n                    local roleA = UnitGroupRolesAssigned(unitA)\n                    local roleB = UnitGroupRolesAssigned(unitB)\n                    \n                    -- Sort melee DPS before melee healers\n                    if roleA ~= roleB then\n                        return roleA < roleB\n                    end\n                end\n                \n                local classA = UnitClassBase(unitA)\n                local classB = UnitClassBase(unitB)\n                \n                local orderA = aura_env.order[classA] or 0\n                local orderB = aura_env.order[classB] or 0\n                \n                if orderA ~= orderB then\n                    return orderA < orderB\n                end\n                \n                return guidA < guidB\n            end\n        )\n        \n        local myAssignmentIndex = tIndexOf(aura_env.affected, WeakAuras.myGUID)\n        local assignment = myAssignmentIndex and aura_env.assignments[myAssignmentIndex]\n        \n        if assignment then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 4.8,\n                expirationTime = GetTime() + 4.8,\n                assignment = assignment,\n                autoHide = true\n            }\n            \n            if aura_env.config.tts then\n                LiquidWeakAuras:PlaySound(assignment)\n            end\n        end\n        \n        aura_env.affected = {}\n        \n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 1243577 then -- Reverse Gravity\n            local unit = aura_env.GUIDToUnit[destGUID]\n            local inRange = UnitInRange(unit)\n            \n            if not inRange then return end\n            \n            table.insert(aura_env.affected, destGUID)\n            \n            if #aura_env.affected == 1 then\n                local id = aura_env.id\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        \n        aura_env.affected = {}\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
