["custom"] = "aura_env.assignmentText = {\n    \"Soak left\",\n    \"Soak middle\",\n    \"Soak right\"\n}\n\naura_env.say = {\n    \"Left\",\n    \"Middle\",\n    \"Right\"\n}\n\nfunction aura_env:ReadNote()\n    aura_env.assignments = {}\n    aura_env.backups = {}\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"Nova\") do\n        local GUIDs = LiquidWeakAuras:LineToGUIDArray(line)\n        \n        if next(GUIDs) then\n            if #aura_env.assignments < 2 then\n                table.insert(aura_env.assignments, GUIDs)\n            else\n                for _, GUID in ipairs(GUIDs) do\n                    tInsertUnique(aura_env.backups, GUID)\n                end\n            end\n        end\n    end\n    \n    aura_env.note = next(aura_env.assignments)\nend",
